INSERT INTO `game_softs` (`id`, `name`, `phonetic`, `phonetic2`, `phonetic_type`, `phonetic_order`, `genre`, `series_id`, `order_in_series`, `original_package_id`, `introduction`, `introduction_from`, `created_at`, `updated_at`) VALUES
(1, '零～zero～', 'ぜろ', 'ぜろ10', 3, 145, 'アクションアドベンチャー', 2, 1, 32, '舞台は、廃墟となった日本家屋「氷室邸」。\r\nプレイヤーは、懐中電灯の明かりだけを頼りに、氷室邸を探索していきます。\r\n障子の裏に、床下に、天井の梁の上に潜む\"何か\"の気配。\r\n懐中電燈の光の中に不意に浮かび上がる影・・・\r\n光と闇が新たな恐怖を織り成します。', '<a href=\"http://www.tecmo.co.jp/product/zero/index2.htm\" target=\"_blank\"><cite title=\"公式サイト\">公式サイト</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:47'),
(3, '零～紅い蝶～', 'ぜろあかいちょう', 'ぜろ20', 3, 146, 'アクションアドベンチャー', 2, 2, 35, '双子の少女が迷い込んだのは・・地図から消えた村\r\n祭りの日　その村は消えた\r\n永遠に続く祭りの夜\r\nその祭りでは　双子が贄とされた\r\n迫る儀式の刻\r\n姉が　妹を　殺す儀式…', '<a href=\"https://www.youtube.com/watch?v=8Wh2bhphpAc\" target=\"_blank\">公式PV</a>', '2018-03-24 10:17:50', '2018-05-29 22:36:47'),
(5, '零～刺青ノ聲～', 'ぜろしせいのこえ', 'ぜろ30', 3, 147, 'アクションアドベンチャー', 2, 3, 38, 'あの日以来、毎晩同じ屋敷に迷い込む夢を見る。\r\n”眠りの家”と呼ばれるその屋敷には、この世のものではない存在達――怨霊が彷徨っていた…\r\n\r\n死んでしまったはずのあの人の姿を見た\r\nあの屋敷の謎を解明すれば、もう一度会えるかもしれない…\r\n黒澤怜は、浮かび上がった写真を手掛かりに、現実の世界で調査を進めていくことになる。', '<a href=\"http://www.tecmo.co.jp/product/zero3/\" target=\"_blank\">公式サイト</a>', '2018-03-24 10:17:50', '2018-05-29 22:36:47'),
(6, '零～月蝕の仮面～', 'ぜろつきはみのかめん', 'ぜろ40', 3, 148, 'アクションアドベンチャー', 2, 4, 40, '私には小さい頃の記憶がない・・・\r\n\r\n幼い頃に何者かに誘拐された、水無月流歌をはじめとるす五人の少女。\r\n十年後、事件に巻き込まれた五人のうちの二人が死亡する。\r\nそして他の二人、海咲と円香は事件のあった「朧月島」に向かうことに。\r\nあの時に何があったのか、そして失った記憶の先に何があるのかを確かめるため、流歌もまた、朧月島をめざす・・・。', '<a href=\"http://www.comshop.co.jp/g-haku/index.php?jan=4902370516715\" target=\"_blank\"><cite title=\"ゲーム博物館\">パッケージ</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:47'),
(7, 'BIOHAZARD', 'ばいおはざーど', 'ばいおはざーど0010', 6, 208, 'サバイバルホラー', 1, 1, 255, 'アメリカ中西部・ラクーンシティで猟奇事件を調査していた特殊部隊「S.T.A.R.S.」の面々は、郊外の森深くの洋館に迷い込み、得体の知れない化け物──ゾンビの襲撃を受ける。\r\n果たして、生還は叶うのか？', '<a href=\"http://www.capcom.co.jp/bio_series/biohazard.html\" target=\"_blank\">旧公式サイト</a>', '2018-03-24 10:17:50', '2018-05-29 22:36:49'),
(8, 'BIOHAZARD2', 'ばいおはざーど2', 'ばいおはざーど0020', 6, 209, 'サバイバルホラー', 1, 2, 264, '新米警官レオンと、クリスの妹クレアがラクーンシティで災禍に巻き込まれる。\r\n街からの脱出を試みる中、彼らが垣間見る事件の裏側とは…？', '<a href=\"http://www.capcom.co.jp/bio_series/biohazard2.html\" target=\"_blank\">旧公式サイト</a>', '2018-03-24 10:17:50', '2018-05-29 22:36:49'),
(9, 'BIOHAZARD3 -LAST ESCAPE-', 'ばいおはざーど3らすとえすけーぷ', 'ばいおはざーど0030', 6, 210, 'サバイバルホラー', 1, 3, 291, '洋館事件後、荒廃した街を訪れたジルは、惨状の中でアンブレラの暗部に迫っていく。\r\nU.B.C.S.隊員らとの奮戦の最中、謎の敵がジルの背後に迫る。', '<a href=\"http://www.capcom.co.jp/bio_series/biohazard3.html\" target=\"_blank\">旧公式サイト</a>', '2018-03-24 10:17:50', '2018-05-29 22:36:49'),
(10, 'BIOHAZARD CODE:Veronica', 'ばいおはざーどこーどべろにか', 'ばいおはざーど0031', 6, 211, 'サバイバルホラー', 1, 4, 306, 'ラクーン脱出から３ヵ月後。兄に習い、アンブレラを追うクレアは、失態から絶海の孤島ロックフォートに投獄される。\r\n混乱の島からの脱出を図るが…。', '<a href=\"http://www.capcom.co.jp/bio_series/biohazard_cv.html\" target=\"_blank\">旧公式サイト</a>', '2018-03-24 10:17:50', '2018-05-29 22:36:49'),
(11, 'biohazard', 'ばいおはざーど', 'ばいおはざーど0032', 6, 212, 'サバイバルホラー', 1, 5, 244, '1998年\r\nラクーンシティ郊外で多発する猟奇殺人事件。\r\n事件捜査中に消息を絶った「S.T.A.R.S.」ブラヴォーチーム。\r\nその捜索に赴いたクリス達アルファチームに異形の群れが襲いかかる。\r\n仲間を失いつつも、かろうじて洋館に駆け込むクリス達。\r\n彼らは生きて此処から脱出することが出来るのだろうか…', '<a href=\"http://store.steampowered.com/app/304240/Resident_Evil__biohazard_HD_REMASTER/\" target=\"_blank\">Steam</a>', '2018-03-24 10:17:50', '2018-05-29 22:36:49'),
(12, 'biohazard0', 'ばいおはざーど0', 'ばいおはざーど0033', 6, 213, 'サバイバルホラー', 1, 6, 246, '猟奇事件調査に向かった「S.T.A.R.S.」ブラヴォーチームのヘリが山間に墜落。\r\n事態収拾をはかるレベッカは停車中の列車を見つけ、乗り込むが…。', '<a href=\"http://www.capcom.co.jp/bio_series/biohazard0.html\" target=\"_blank\">旧公式サイト</a>', '2018-03-24 10:17:50', '2018-05-29 22:36:49'),
(13, 'BIOHAZARD -OUTBREAK-', 'ばいおはざーどあうとぶれいく', 'ばいおはざーどあうとぶれいく1', 6, 218, 'サバイバルホラー', 1, 20, 295, '9月某日。アメリカ中西部、ラクーンシティ。\r\n突如発生した未曾有の生物災害（バイオハザード）により、街は地獄と化した。\r\n刻々と身体を蝕むウイルスの侵食。\r\n道々にあふれ返る死者どもの進行。\r\n恐怖と絶望が、街の通りを埋め尽くす。\r\nこれは、明かされなかった真実の一面。\r\n数奇なる運命をたどった、ある市民たちの逃走劇である。', '<a href=\"http://www.jp.playstation.com/software/title/slpm65428.html\" target=\"_blank\"><cite title=\"プレイステーションオフィシャルサイト\">PSソフトウェアカタログ</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:49'),
(14, 'biohazard4', 'ばいおはざーど4', 'ばいおはざーど0040', 6, 214, 'サバイバルホラー', 1, 7, 312, '未曾有の生物災害“ラクーン事件”。\r\nその経緯は白日にさらされ、元凶となったアンブレラ社は崩壊した。 \r\n事件から生還したレオン・S・ケネディは大統領直轄のエージェントへと成長していた。\r\n彼は誘拐された大統領令嬢の目撃情報を元に ヨーロッパの寒村へと向かう。\r\n不穏な村で新たな事件の幕が上がる…。', '<a href=\"http://www.capcom.co.jp/bioDL/4/ja/\" target=\"_blank\">公式サイト</a>', '2018-03-24 10:17:50', '2018-05-29 22:36:49'),
(15, 'BIOHAZARD -OUTBREAK File2-', 'ばいおはざーどあうとぶれいく2', 'ばいおはざーどあうとぶれいく2', 6, 219, 'サバイバルホラー', 1, 21, 298, 'もう一度、あの闇へ・・・。\r\nラクーンシティで暮らす、ごく平凡な市民に襲いかかる「厄災」。\r\n共に行動していた仲間が、一人、また一人と力尽きていく。\r\nはぐれた末に、いつの間にか物言わぬ死体となっている。\r\n共に行動する仲間がいるからこそ襲いかかる、不安、孤独、恐怖・・・。\r\n新たな敵、新たな恐怖、新たな真実に出会える、完全新作シナリオを伴って、極上のホラーエンターテイメント第二幕が、今始まる・・・。', '<a href=\"http://www.jp.playstation.com/software/title/slpm65692.html\" target=\"_blank\"><cite title=\"プレイステーションオフィシャルサイト\">PSソフトウェアカタログ</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:49'),
(16, 'BIOHAZARD Deadly Silence', 'ばいおはざーどでっどりーさいれんす', 'ばいおはざーどでっどりーさいれんす', 6, 224, 'サバイバルホラー', 1, 30, 299, '初代「バイオ」をＤＳのタッチスクリーン向けにアレンジ。\r\n敵の配置が異なるリバースモード、４人までの通信プレイに対応する等、多彩な追加要素を備えている', '<a href=\"http://www.capcom.co.jp/bio_series/biohazard_ds.html\" target=\"_blank\">旧公式サイト</a>', '2018-03-24 10:17:50', '2018-05-29 22:36:50'),
(17, 'バイオハザード アンブレラ・クロニクルズ', 'ばいおはざーどあんぶれらくろにくるず', 'ばいおはざーどあんぶれらくろにくるず10', 6, 220, 'サバイバルホラー', 1, 40, 261, '「黄道特急事件」（『バイオハザード0』）、「洋館事件」（第1作『バイオハザード』）、「ラクーン市壊滅事件」（『バイオハザード3』）、そしてナンバリングシリーズでは語られていないオリジナルエピソード、「アンブレラ終焉」を楽しむ事ができる。\r\nガンシューティングならではの迫力、爽快感が楽しめるのはもちろん、「バイオハザード」らしい演出やアクションイベントも登場する。', '<a href=\"http://www.capcom.co.jp/bhcs/\" target=\"_blank\">PS3版公式サイト</a>', '2018-03-24 10:17:50', '2018-05-29 22:36:49'),
(18, 'BIOHAZARD5', 'ばいおはざーど5', 'ばいおはざーど0050', 6, 215, 'サバイバルホラー', 1, 8, 47, 'アンブレラ社の生物兵器流出により、引き起こされた未曾有の生物災害「ラクーン事件」から約10年。\r\n事件の生還者、クリス・レッドフィールドは対バイオテロ部隊BSAAのエージェントとして活躍していた。\r\n彼はアフリカ某国、キジュジュ自治区での生物兵器密売人の拘束作戦への参加を契機に、この地で進行する巨大な陰謀、そしてウェスカーの関与を知ることなる…。', '<a href=\"http://www.capcom.co.jp/bioDL/5/ja/\" target=\"_blank\">公式サイト</a>', '2018-03-24 10:17:50', '2018-05-29 22:36:49'),
(19, 'バイオハザード ダークサイド・クロニクルズ', 'ばいおはざーどだーくさいどくろにくるず', 'ばいおはざーどあんぶれらくろにくるず20', 6, 221, 'サバイバルホラー', 1, 41, 15, 'レオンの回想を通して『バイオハザード2』『バイオハザード コード：ベロニカ』のエピソード、そしてオリジナルのエピソード『オペレーション・ハヴィエ』が語られる。\r\n『オペレーション・ハヴィエ』では　『バイオハザード4』ではレオンと敵対する事となるクラウザーが登場、レオンとチームを組んで任務に挑む。\r\n美しさを増したグラフィックに加えて、シェイクカメラの導入により、更に臨場感あふれるホラーガンシューティングが楽しめる。', '<a href=\"http://www.capcom.co.jp/bhcs/\" target=\"_blank\">PS3版公式サイト</a>', '2018-03-24 10:17:50', '2018-05-29 22:36:49'),
(20, '九怨-kuon-', 'くおん', 'くおん1', 2, 68, '', NULL, NULL, 112, '夜な夜な聞こえてくる、鼓の調べとおさなごの唄い声。\r\n亡霊のように彷徨い歩く住人と、闇に蠢く餓鬼たち。\r\nその屋敷に足を踏み入れた者は、二度と戻ってくることはなかった。\r\nプレイヤーは、異形が蠢く屋敷を舞台に2人の主人公を操作し、恐怖に襲われながら物語の真実を解き明かします。\r\n入り組んだ屋敷の作り出す死角、得体の知れない影、見え隠れする人影など、様々なシチュエーションが心理的恐怖を誘う、怪談アクションです。', '<a href=\"http://www.fromsoftware.jp/jp/detail.html?csm=032\" target=\"_blank\">公式サイト</a>', '2018-03-24 10:17:50', '2018-05-29 22:36:48'),
(21, 'ヴァンパイア・レイン', 'ばんぱいあれいん', 'ばんぱいあれいん1', 6, 238, '', NULL, NULL, 238, '毎年、世界で行方不明となっている何百万人という人々。\r\n彼らはいったいどこへ…?\r\nそれらはすべて闇夜に暗躍する\"ナイトウォーカー\"の仕業。驚異的な戦闘能力と残忍さを兼ね備えた彼らがこのまま増え続ければ、人類に未来は無いだろう。\r\n\"ナイトウォーカー\"が能力を低下させる雨の一夜に、全人類の全てを賭けた、負けることの許されない戦いが、静かに始まる。', '<a href=\"https://www.amazon.co.jp/AQ%E3%82%A4%E3%83%B3%E3%82%BF%E3%83%A9%E3%82%AF%E3%83%86%E3%82%A3%E3%83%96-15782621-%E3%83%B4%E3%82%A1%E3%83%B3%E3%83%91%E3%82%A4%E3%82%A2%E3%83%AC%E3%82%A4%E3%83%B3-%E3%82%A2%E3%83%AB%E3%82%BF%E3%83%BC%E3%83%89%E3%82%B9%E3%83%94%E3%83%BC%E3%82%B7%E3%83%BC%E3%82%BA-PS3/dp/B000E0XNBY\" target=\"_blank\"><cite title=\"Amazon\">Amazon(PS3版)</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:48'),
(22, 'アローン・イン・ザ・ダーク', 'あろーんいんざだーく', 'あろーんいんざだーく1', 1, 16, '', 3, 1, 31, '1924年、芸術家のジェレミー・ハートウッドがルイジアナ州のデルセト屋敷にて自殺し、その生涯を閉じた。\r\n現地の警察は現状から見て自殺と断定し発表した。\r\nしかしながら、それはあくまで表向きの話で、本当は誰もが心の奥では感じていた。\r\n\r\n「あの屋敷には何か恐ろしい事があったに違いない。何か、とてつもなく恐ろしい事が…」\r\n\r\nジェレミーの自殺事件から数日後。\r\n古物商のグロリア・アレンから調査の依頼を受けた私立探偵エドワード・カーンビーは、狂気と呪いに満ちた屋敷へと向かう…。\r\n\r\n同じ頃、ジェレミー・ハートウッドの姪であるエミリー・ハートウッドもこの事件を不審に思い、やはり屋敷へと向かうのであった。', '<a href=\"https://ja.wikipedia.org/wiki/%E3%82%A2%E3%83%AD%E3%83%BC%E3%83%B3%E3%83%BB%E3%82%A4%E3%83%B3%E3%83%BB%E3%82%B6%E3%83%BB%E3%83%80%E3%83%BC%E3%82%AF\" target=\"_blank\"><cite title=\"Wikipedia 2018年3月20日 (火) 15:22 更新版\">Wikipedia</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:47'),
(23, 'アローン・イン・ザ・ダーク2', 'あろーんいんざだーく2', 'あろーんいんざだーく2', 1, 17, '', 3, 2, 405, '「呪われた屋敷ヘルズ・キッチンにグレース・ソーンダースはいる。やはり片目のジャックだ。助けてくれ…　ストライカー」\r\nそれは私の友人で探偵仲間でもあるストライカーからの電報だった。\r\n彼はグレース嬢誘拐事件を調査中に消息を絶っていたのだ。\r\n私は愛用のコルト・リボルバーをふところに押し込むと、ヘルズ・キッチンへと向かった。\r\n巨大な欲望と魔術の影が渦巻く呪われた屋敷の中へと…', 'SS版パッケージ', '2018-03-24 10:17:50', '2018-05-29 22:36:47'),
(25, 'アローン・イン・ザ・ダーク', 'あろーんいんざだーく', 'あろーんいんざだーく3', 1, 18, '', 3, 5, 50, '市民のためのセントラルパークと言うが……それは嘘だ。\r\nセントラルパークは、ニューヨーク市民の憩いの場所として作られたのではない。\r\nまったく別の者たちのために作られたのだ。\r\nこの真実を隠し通すことは、もはや不可能となった。\r\n\r\n世界が週末を迎えるような一夜がはじまる――。\r\nエドワード・カーンビーは、想像をはるかに超えた存在と戦い、セントラルパークに封印された天地を揺るがす秘密を暴き出す。', '<a href=\"http://www.comshop.co.jp/g-haku/index.php?jan=4938833009050\" target=\"_blank\"><cite title=\"ゲーム博物館\">パッケージ</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:48'),
(26, 'エコーナイト', 'えこーないと', 'えこーないと1', 1, 38, '', 4, 1, 76, '主人公リチャードは、火事で焼け落ちた父の家を探索している中で、客船の絵を目にする。\r\n次の瞬間24年前に消息不明になったはずの豪華客船「オルフェウス号」の船上にいた。\r\nプレイヤーはリチャードとなり、船内を彷徨い続ける亡霊たちが抱える数々の未練を解き明かしながら、客船からの脱出を試みる主観視点のリアルタイムアドベンチャーゲームです。', '<a href=\"http://www.fromsoftware.jp/jp/detail.html?csm=007\" target=\"_blank\"><cite title=\"公式サイト\">公式サイト</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:47'),
(27, 'エコーナイト#2 眠りの支配者', 'えこーないと2ねむりのしはいしゃ', 'えこーないと2', 1, 39, '', 4, 2, 79, '恋人であるクリスティーナが姿を消してから3ヶ月。\r\n進展を見せない警察の捜査に業を煮やしたリチャードは、彼女がいなくなった前日に立ち寄ったという図書館へ車を走らせる。\r\nそこでリチャードは、一冊の色褪せた本を見つけた。\r\nその中には恋人とそっくりな女性の姿が写っていた—。\r\nその場所に留まり続けている魂たちを解放し、魂たちから得られるヒントをもとに、洋館で起こった悲しい真実を解き明かすリアルタイムアドベンチャーゲームです。', '<a href=\"http://www.fromsoftware.jp/jp/detail.html?csm=011\" target=\"_blank\"><cite title=\"公式サイト\">公式サイト</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:47'),
(28, 'NEBULA -ECHO NIGHT-', 'ねびゅらえこーないと', 'ねびゅらえこーないと', 5, 206, '', 4, 3, 232, '2044年、月面。\r\n月軌道上を航行中の宇宙旅客機が、突如消息を絶った。\r\n懸命の捜索にも関わらず、旅客機はついに発見されず、乗客・乗員の行方も不明のままであった。\r\n深い孤独と悲しみを抱え、さまよい続ける無数の霊。\r\n主人公リチャードは、姿を消した恋人の姿を求め、廃墟と化した月の施設へと足を踏み入れる。\r\n月面施設を探索し、監視モニターを活用しながら、亡霊たちの残した想いを叶えていくリアルタイムアドベンチャーです。', '<a href=\"http://www.fromsoftware.jp/jp/detail.html?csm=030\" target=\"_blank\"><cite title=\"公式サイト\">公式サイト</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:47'),
(29, 'michigan', 'みしがん', 'みしがん1', 7, 270, '', NULL, NULL, 146, '8月20日、シカゴを中心としたミシガン湖南西部周辺に異常濃霧が発生し、政府は発生地域周囲への避難勧告を発令した。\r\nこの政府の発令について具体的な説明がなく、濃霧発生地域内の状況も、情報が遮断されたかのような状態になっていた。\r\n\r\n報道局である「ZaKa TV」が、この異常事態に何か裏があると推測。スクープを取るため、報道レポーター、音声スタッフ、そしてカメラマンを濃霧発生地域の中心であるシカゴに送り込む。\r\n濃い霧で包まれた街の中、報道クルーは凄惨な光景、そして人を襲う異形の怪物を目の当たりにする。\r\nそれでもなお報道を続けカメラを回していくが、クルーたちに次々と恐怖が襲いかかる。', '<a href=\"https://ja.wikipedia.org/wiki/Michigan_(%E3%83%93%E3%83%87%E3%82%AA%E3%82%B2%E3%83%BC%E3%83%A0)\" target=\"_blank\"><cite title=\"Wikipedia 2017年10月15日 (日) 08:49更新版\">Wikipedia</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:50'),
(34, 'R?MJ THE MYSTERY HOSPITAL', 'えむじぇいざみすてりーほすぴたる', 'えむじぇいざみすてりーほすぴたる', 1, 44, '', NULL, NULL, 84, '99年夏、巨大総合病院で起きた大爆発。\r\nこの原因不明の大爆発に巻き込まれ、逃げ遅れてしまったハジメは、親友のリョウとトモヲ、新人看護婦アヤ、そして突然現れた謎の美少女ミサトの4人と共にそこから大脱出をすることになる。\r\n彼等は無事に脱出できるのだろうか…', '<a href=\"http://www.jp.playstation.com/software/title/slps91096.html\" target=\"_blank\"><cite title=\"プレイステーションオフィシャルサイト\">PSソフトウェアカタログ</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:48'),
(35, '・・・いる！', 'いる', 'いる', 1, 28, '', NULL, NULL, 74, '陸の孤島にある霧ヶ丘高校に通う主人公・稲葉達也は、不気味な夢を見る。\r\nそれは、礼拝堂にいる女子生徒・相川かおりから「月食が起きる前に逃げて」という警告を受けるものだった。\r\nその言葉が脳裏に残ったまま学園祭を明日に控え、準備に再び取りかかった達也は手伝ってもらおうと他の生徒を呼びに行くが、夜空では月食が始まっていた。\r\n周囲の生徒たちが狂い始め、さまざまなモンスターが襲ってくる状況から達也は無事に脱出し、校内に隠された謎を解き明かすことができるのか。', '<a href=\"https://ja.wikipedia.org/wiki/%E2%80%A6%E3%81%84%E3%82%8B!\" target=\"_blank\"><cite title=\"Wikipedia 2018年2月10日 (土) 07:48更新版\">Wikipedia</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:48'),
(39, 'es', 'えす', 'えす', 1, 40, '', NULL, NULL, 81, '連続誘拐殺人事件、第7の犠牲者を救え!!', 'パッケージ', '2018-03-24 10:17:50', '2018-05-29 22:36:48'),
(40, 'エターナル・ダークネス', 'えたーなるだーくねす', 'えたーなるだーくねす', 1, 42, '', NULL, NULL, 82, '太古の地球を支配し、数千年の時を隔てて復活を企てる「エンシャント」。\r\nそして、その野望をくい止めようと立ち向かう１２人の選ばれし者たち…。\r\n２０００年を越える歴史と世界中のロケーションを舞台に、彼等は人類の存続をかけて、人知を超えた存在「エンシャント」に立ち向かう。', '<a href=\"https://www.nintendo.co.jp/ngc/gedj/index.html\" target=\"_blank\"><cite title=\"公式サイト\">公式サイト</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:48'),
(41, '歸らずの森', 'かえらずのもり', 'かえらずのもり', 2, 52, '', NULL, NULL, 93, '消えた友人を探すために「名も無き森」の館に入った三人。\r\n館の怨念の真実を解き無事友人を助けて脱出できるのか!?', '<a href=\"http://www.gae.co.jp/arc/1493\" target=\"_blank\"><cite title=\"公式サイト\">公式サイト</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:48'),
(42, '学校であった怖い話', 'がっこうであったこわいはなし', 'がっこうであったこわいはなし10', 2, 53, '', 34, 1, 94, '「今度の新聞で、うちの高校の七不思議の特集を組もうぜ」。\r\n高校一年生の主人公が所属する新聞部の部長のひとことが、この物語の発端でした。\r\n放課後、話を聞くために7人の話し手が集められたはずが、部室にはなぜか6人しかいません。\r\n漂う重い空気の中、6人の恐ろしい話が始まります……。 ', '<a href=\"https://www.nintendo.co.jp/titles/20010000007727\" target=\"_blank\"><cite title=\"任天堂\">Wii U版公式サイト</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:48'),
(44, '恐怖新聞', 'きょうふしんぶん', 'きょうふしんぶん1', 2, 63, '', 35, 1, 108, 'ポルターガイストによって、夜毎配達される「恐怖新聞」。\r\n読むたびに命が百日縮まる。\r\nあなたは、ポルターガイストにとりつかれたのです。', 'パッケージ', '2018-03-24 10:17:50', '2018-05-29 22:36:48'),
(45, '恐怖新聞【平成版】怪奇！心霊ファイル', 'きょうふしんぶんへいせいばんかいきしんれいふぁいる', 'きょうふしんぶん2', 2, 64, '', 35, 2, 109, '読むごとに命が100日づつ縮んでしまうという恐怖新聞を配達する悪霊にとり憑かれてしまったプレイヤーは、次々と起こる不幸な事件の謎を解くため、夜の学校や病院、トンネルなどを懐中電灯ひとつで探索する。', '<a href=\"https://www.amazon.co.jp/%E6%81%90%E6%80%96%E6%96%B0%E8%81%9E-%E5%B9%B3%E6%88%90%E7%89%88-%E6%80%AA%E5%A5%87-%E5%BF%83%E9%9C%8A%E3%83%95%E3%82%A1%E3%82%A4%E3%83%AB-%E3%82%B3%E3%83%8A%E3%83%9F/dp/B00022FISK\" target=\"_blank\">Amazon</a>', '2018-03-24 10:17:50', '2018-05-29 22:36:48'),
(46, 'グレゴリーホラーショー ソウルコレクター', 'ぐれごりーほらーしょーそうるこれくたー', 'ぐれごりーほらーしょーそうるこれくたー', 2, 73, '', NULL, NULL, 113, 'グレゴリーハウスへようこそ\r\n霧の深い森の中のホテルに迷い込んだあなた。\r\nそこであなたを待ちうけていたのは、うさんくさい管理人グレゴリー。\r\nあなたを苦しめるブキミで奇妙な宿泊客たち…\r\n\r\n元の世界へ帰るため、あなたは、迷えるタマシイを集める約束を、死神と交わします。\r\nそれを阻止しようと、宿泊客がさまざまなジャマを仕掛けてきて…\r\n\r\n徐々に明らかになっていくホテルの秘密。\r\nそして、最後に明かされる――あなたがこのホテルに来た、その理由とは？', '<a href=\"http://www.comshop.co.jp/g-haku/index.php?jan=4976219958448\" target=\"_blank\"><cite title=\"ゲーム博物館\">パッケージ</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:48'),
(47, 'クロックタワー', 'くろっくたわー', 'くろっくたわー10', 2, 75, '', 7, 1, 114, '北欧の山間部にひっそりとたたずむ古びた屋敷。\r\n土地の人々はそれを「CLOCK TOWER」と呼んでいた。\r\n孤児の美少女ジェニファーは、14才の誕生日にこの屋敷で養育されることが決まる。\r\n3人の友人と共に「CLOCK TOWER」へ赴いたジェニファーが見たものとは・・・・・・。\r\n次々に襲われていく友人達！\r\nそして今、巨大な鋏がジェニファーへも迫る。', 'SFC版パッケージ', '2018-03-24 10:17:50', '2018-05-29 22:36:47'),
(48, 'クロックタワー2', 'くろっくたわー2', 'くろっくたわー20', 2, 76, '', 7, 2, 117, 'ノルウェー全土を震撼させた猟奇的殺人事件。\r\n「CLOCK TOWER事件」から1年…奴が、再び現れた！\r\n主人公はごく普通の少女ジェニファーと、彼女の保護者であるヘレンの2人。\r\nごく平凡な彼女たちの日常生活が、かつて倒したはずの「シザーマン」の出現によって一気に闇の世界へと変貌する。\r\n再び殺人を繰り返す「シザーマン」。\r\nはたして彼女たちは復讐に燃える奴の巨大なハサミから逃げ延び、その不死身の謎を解いてこの世から追放できるのか…？', 'パッケージ', '2018-03-24 10:17:50', '2018-05-29 22:36:47'),
(49, 'クロックタワー・ゴーストヘッド', 'くろっくたわーごーすとへっど', 'くろっくたわー21', 2, 77, '', 7, 3, 119, '1999年、春。類稀なるその事件は・・・\r\nある事件を機に、弁天町に住む父の友人宅に預けられることとなった女子高生、〈御堂島 優〉。\r\nそんな彼女の周辺で次々と引き起こされる惨劇の数々・・・。\r\n緑色の肌、黄色い血液の謎。\r\nそして、「才堂家の呪い」とは？\r\n1999年、春。類稀なるその事件は・・・もう始まっていた。', 'パッケージ', '2018-03-24 10:17:50', '2018-05-29 22:36:48'),
(50, 'クロックタワー3', 'くろっくたわー3', 'くろっくたわー30', 2, 78, '', 7, 4, 120, '時は2003年、英国はロンドンの下宿館で物語は始まる。一人の少女が、自らの宿命ゆえに体験する恐怖・・・。\r\n少女の名は、アリッサ。”魔のモノの配下”たちが支配する世界に迷い込む。\r\n今作は監督及び演技指導にあの深作欣二監督を起用！\r\n美しく華麗、精細かつダイナミックなカメラワークで新たな恐怖の世界を生み出している。\r\n子供がやってはいけない、鬼ごっこがあるのを知っていますか？', '<a href=\"http://www.jp.playstation.com/software/title/slpm65221.html\" target=\"_blank\"><cite title=\"プレイステーションオフィシャルサイト\">PSソフトウェアカタログ</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:48'),
(51, '黒の断章 THE LITERARY FRAGMENT', 'くろのだんしょうざりてらりーふらぐめんと', 'くろのだんしょうざりてらりーふらぐめんと', 2, 80, 'パースペクティヴモダンホラーアドベンチャー', 44, 1, 158, '都内某所。入居者数がそろわないままの不景気なマンション。\r\n互いの顔さえ知らぬ入居者達・・・・。\r\n突如起こった一家惨殺事件。\r\n好奇心から事件に巻き込まれる探偵、涼崎（すずさき）と草薙（クサナギ）。\r\nそこには何の関連も――あるはずが無かった。', '<a href=\"http://www.getchu.com/soft.phtml?id=137781\" target=\"_blank\">Getchu.com</a>', '2018-03-24 10:17:50', '2018-05-29 22:37:28'),
(52, 'SIREN', 'さいれん', 'さいれん10', 3, 98, '', 8, 1, 165, '内陸の山間部にひっそりと存在する、人口1500人にも満たない寒村……××県三隅群羽生蛇村。\r\n小雨の降りしきるある新月の夜、サイレンにも似た、唸り声のようでもある奇怪な音が響きわたり、住民とともに村は消えた。\r\n恐怖と混乱のさなか、やがて消失に巻き込まれた村人たちは、自分たちを支配している恐るべきルールの存在を知る。\r\n「徐々に、自分たちの身体が変容し始めていて、海から奇怪な音が聞こえてくる時、誰かが人間ではなくなる」と……。\r\n人でなくなった者たちはやがて共同体を築き、人のような生活を始める。\r\nあたかも人間とすり替わっていくように。\r\nそんな中、残された人々は生き残るための絶望的な戦いを強いられるのだった——。', '<a href=\"http://www.jp.playstation.com/software/title/jp9000npjc00001_000000000000000000.html\" target=\"_blank\">公式サイト</a> ※短縮のため、一部削除しました。', '2018-03-24 10:17:50', '2018-05-29 22:36:48'),
(53, 'SIREN2', 'さいれん2', 'さいれん20', 3, 99, '', 8, 2, 168, '日本近海の離島「夜見島」。\r\n29年前、一夜にして無人島となった、廃墟の島。\r\n島民たちはどこへ。なぞは解かれぬまま、月日だけが流れる。\r\n\r\nそして現代。何かに導かれるように、島を目指す人々。\r\n―深夜0:00。サイレンが島を覆い、惨劇の夜が蘇る。', '<a href=\"http://www.comshop.co.jp/g-haku/index.php?jan=4948872151061\" target=\"_blank\"><cite title=\"ゲーム博物館\">パッケージ</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:37:29'),
(54, 'SIREN:New Translation', 'さいれんにゅーとらんすれいしょん', 'さいれん21', 3, 100, '', 8, 3, 170, 'PlayStation 2専用ソフト『SIREN』からその舞台設定を引き続きつつも、その演出・ストーリーテリング・シナリオなど全てを抜本的に再構築。\r\nまるで海外TVドラマのように構成され、スピーディーに、かつスリリングに展開するシナリオ、外国人登場人物の視線を通すことで倍増された日本ならではの風土への畏怖と恐怖…極上の恐怖エンタテインメントの誕生。', '<a href=\"http://www.jp.playstation.com/software/title/bcjs30020.html\" target=\"_blank\">公式サイト</a>', '2018-03-24 10:17:50', '2018-05-29 22:36:48'),
(55, 'The FEAR', 'ざふぃあー', 'ざふぃあー', 3, 117, '', NULL, NULL, 194, '関東地方T県G郡の山奥にある洋館。\r\nこの洋館は日露戦争前にロシアの公館として建てられたといわれていて、そこで過去に殺人事件が起こったという噂がある。\r\nこの洋館に主人公達が心霊番組の撮影のため訪れるところから物語が始まる。', '<a href=\"https://ja.wikipedia.org/wiki/The_FEAR\" target=\"_blank\"><cite title=\"Wikipedia 2014年7月5日 (土) 03:39更新版\">Wikipedia</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:48'),
(56, 'Juggernaut～戦慄の扉～', 'じゃがーのーとせんりつのとびら', 'じゃがーのーとせんりつのとびら', 3, 127, '', NULL, NULL, 204, '「魔（エビル）」に肉体と精神を乗っ取られた恋人を救うため、主人公は彼女の心と一体化し、彼女の精神世界の中にサイコダイブした。\r\n彼は、不思議な島の砂浜で覚醒した。\r\nさ迷ううちに彼の前には不思議な館が現れる。\r\n何とか館の中に入った彼の前に現れるいくつかの扉。\r\n彼は第一の扉を開けた……。\r\n\r\n扉を開けた……\r\n　あなたを待つものは……', 'パッケージ', '2018-03-24 10:17:50', '2018-05-29 22:36:49'),
(57, 'THEホラーミステリー～惨劇館ケビン伯爵の復活～', 'ざほらーみすてりーさんげきかんけびんはくしゃくのふっかつ', 'ざほらーみすてりーさんげきかんけびんはくしゃくのふっかつ', 3, 118, '', NULL, NULL, 195, 'ホラー雑誌の代名詞ともいえる「ホラーＭ」に執筆中の御茶漬海苔氏が監修！\r\n御茶漬海苔氏の代表作でもある短編集の「惨劇館」の中でも根強い人気シリーズである「ケビン伯爵の惨劇」をベースにオリジナルストーリーが展開される。\r\n物語は全１３章で構成され、各章に数種類のエンディングが用意されている。\r\nストーリーの展開次第では、読めない章やエピローグもあり、様々な物語が楽しめる。\r\n\r\n今宵の惨劇は君に訪れる・・・！', '<a href=\"http://tsutaya.tsite.jp/item/game/PTA0000FD6ZE\" target=\"_blank\">TSUTAYA</a>', '2018-03-24 10:17:50', '2018-05-29 22:36:48'),
(58, 'THE恋愛ホラーアドベンチャー-漂流少女-', 'ざれんあいほらーあどべんちゃーひょうりゅうしょうじょ', 'ざれんあいほらーあどべんちゃーひょうりゅうしょうじょ', 3, 120, '', NULL, NULL, 198, '地底に沈んだ廃校で少年と5人の少女達の生存を賭けたアドベンチャーが始まる。\r\n闇と静寂・・・そして悪しきモノが彼らに牙をむいて立ちはだかる。\r\n\r\n廃校に秘められた謎、そして忌まわしき過去。\r\n5人の少女から探索パートナーを選び、脱出のための謎を解き明かせ。\r\n『あの娘』と無事に元の生活に帰ることができるのだろうか！？\r\n\r\n空腹によって追い詰められていく・・・\r\n限られた食料の分配が生存のカギを握る。\r\n彼らは残された食料の重みを知ることとなる・・・', '<a href=\"https://www.d3p.co.jp/s_20/s20_034.html\" target=\"_blank\">公式サイト</a>', '2018-03-24 10:17:50', '2018-05-29 22:36:49'),
(59, 'スウィートホーム', 'すうぃーとほーむ', 'すうぃーとほーむ', 3, 138, '', NULL, NULL, 208, '何人生きて脱出できるのだろう……。。\r\n呪われた屋敷・間宮邸。\r\n今はもう誰も住まないこの屋敷を訪れた5人のTV局のスタッフに襲いかかる数々の恐怖。\r\nドアが閉じ、天井が崩れ、現れる間宮家の霊。\r\n出口を求めて、5人の戦いが始まった。\r\n果たして彼らは出口を見つけることができるだろうか。\r\n何人がこの屋敷から生きて出られるのだろうか。', 'パッケージ', '2018-03-24 10:17:50', '2018-05-29 22:36:49'),
(60, 'DARKSEED', 'だーくしーど', 'だーくしーど1', 4, 154, '', 9, 0, 213, '主人公マイク・ドーソンは都会の喧騒を逃れてウッドランドヒル郊外の古い屋敷を購入した。\r\n悪夢はここからはじまる。\r\n沈黙のベールに包まれたこの屋敷全体に漂う邪悪な影、不吉な気配、そして悪夢の謎を追って、今あなたは時空を越える。\r\n全ての現象は表裏一体となり、関連し合っている。\r\n3日間という短い間に解かなければならない謎、時間を追うごとに高まる緊張感、自分の鼓動にさえも怯えながら、あなたはこのゲームを続けなければならない。\r\nそう……あなたは、もうダークワールドから逃れられない。', 'パッケージ', '2018-03-24 10:17:50', '2018-05-29 22:36:48'),
(61, 'DARKSEEDII', 'だーくしーど2', 'だーくしーど2', 4, 155, '', 9, 0, 214, '進学校でもなく、いわゆるお嬢さま学校でもなく。\r\nあまり知られていない伝統がある学園へと、季節はずれに赴任する事になった主人公。\r\nその学園には、生徒間で語り継がれる「魔女伝説」があった。\r\nよくある七不思議の類だと気にも留めなかった主人公だが、ある日事件が起きる。\r\nオカルトじみた道具立てによって、怪我人が出たのだ。\r\n犯人も、その動機も不明なまま、学園にまつわる伝説の再来だとささやかれ始める。\r\n行き過ぎた悪戯だと、事を荒立てまいとする教師達。\r\nしかし、その対応を嘲笑うかのように、同じような事件が続く。\r\nいったい誰が、なんのために？\r\n主人公は独自に捜査をはじめる。', '<a href=\"http://www.comshop.co.jp/g-haku/index.php?jan=4582154170045\" target=\"_blank\"><cite title=\"ゲーム博物館\">パッケージ</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:48'),
(62, 'DARK TALES From The Lost Soul', 'だーくているずふろむざろすとそうる', 'だーくているずふろむざろすとそうる', 4, 157, '', NULL, NULL, 217, '現実と夢のわずかな隙間に存在する、THE LOST SOUL。\r\n当倶楽部は、最後の旅立ちを前にしたお客様をお迎えする、憩いの場。\r\nここには、あなたが結末を決める三つのストーリーが用意されている。\r\n悪夢。謎。恐怖。あなたは戻ってくることができるのか？', 'パッケージ', '2018-03-24 10:17:50', '2018-05-29 22:36:49'),
(63, '晦-つきこもり-', 'つきこもり', 'つきこもり', 4, 166, '', NULL, NULL, 224, '春休み、主人公・葉子の祖母の七回忌の日。\r\n親戚が一堂に会する中、一族の一人、真田泰明が突然口を開いた。\r\n「七回忌の夜、怖い話をし合うと死者が蘇るんだそうだ」\r\n\r\n一同は空き間を見つけ移動。\r\n使われていないはずの空き間が賑わっているのを不審に思って覗いた本家の嫁前田和子が怖い話の参加者に加わる。\r\n\r\n「この客間のこと知ってるの? ちょっとした曰くつきなのよ。それはね……」', '<a href=\"https://ja.wikipedia.org/wiki/%E3%83%80%E3%83%BC%E3%82%AF%E3%83%A1%E3%82%B5%E3%82%A4%E3%82%A2\" target=\"_blank\"><cite title=\"Wikipedia 2018年3月15日 (木) 15:08更新版\">Wikipedia</cite></a> ※短縮のため、一部改変', '2018-03-24 10:17:50', '2018-05-29 22:36:49'),
(64, 'Dの食卓', 'でぃーのしょくたく', 'でぃーのしょくたく1', 4, 169, '', 10, 1, 463, '1997年ロサンゼルスの総合病院で大量殺人事件が発生した。\r\n犯人は同病院の病院長リクター・ハリス。\r\n今もなお、多数の入院患者を人質にとって病院内に立てこもっているため、手を出すことができない。\r\nそこへ現れる一人の女性、リクターの娘ローラ・ハリス。\r\n変貌した父の謎を突き止めるため、惨劇の舞台である病院に単身乗り込むのだが…', 'パッケージ', '2018-03-24 10:17:50', '2018-05-29 22:36:49'),
(65, 'Dの食卓2', 'でぃーのしょくたく2', 'でぃーのしょくたく2', 4, 170, '', 10, 2, 231, '2000年、クリスマスのカナダ。\r\nその上級で、主人公・ローラの乗った飛行機が墜落事故に遭う。\r\n同じ飛行機に乗っていた黒人女性・キンバリーに助けられ、吹雪の山小屋で目覚めるローラ。\r\nやがて二人は、身の毛もよだつ姿に変貌した人間＝モンスターに遭遇する。\r\n何故、人間がモンスターになってしまったのか。\r\n何故、あの事故が起こったのか。\r\n謎を解いて行くにつれ、宇宙、地球、人類の運命に関わる壮大なストーリーが徐々にその姿を現してゆく――', 'パッケージ', '2018-03-24 10:17:50', '2018-05-29 22:36:49'),
(66, 'デビルマン', 'でびるまん', 'でびるまん', 4, 184, '', NULL, NULL, 240, '「君に恐怖の遺産を受け取ってほしい・・・・」\r\n親友・飛鳥 了の奇妙な言葉に誘われ、不動 明は飛鳥邸へ出向いた。\r\nその後起こる、恐ろしい運命が待ちうけているとも知らずに・・・・。', '<a href=\"https://www.amazon.co.jp/%E3%83%90%E3%83%B3%E3%83%80%E3%82%A4-%E3%83%87%E3%83%93%E3%83%AB%E3%83%9E%E3%83%B3/dp/B00005OVJI%3FSubscriptionId%3DAKIAINELD3CSNUTU2R4Q%26tag%3Dh-g-s-22%26linkCode%3Dxm2%26camp%3D2025%26creative%3D165953%26creativeASIN%3DB00005OVJI\" target=\"_blank\"><cite title=\"Amazon\">パッケージ</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:49'),
(67, 'トワイライトシンドローム-探索編-', 'とわいらいとしんどろーむたんさくへん', 'とわいらいとしんどろーむ10', 4, 193, '', 11, 1, 280, '好奇心旺盛な女子高生、「ユカリ」「チサト」「ミカ」の3人は、様々な怖い噂の真相を探るべく心霊スポットや噂のあった現場へと\"探索\"にでかける。\r\nそこで、彼女達が見た真実とは…', 'パッケージ', '2018-03-24 10:17:50', '2018-05-29 22:36:49'),
(68, 'トワイライトシンドローム-究明編-', 'とわいらいとしんどろーむきゅうめいへん', 'とわいらいとしんどろーむ20', 4, 194, '', 11, 2, 281, 'ミカ：センパイ、聞いたこと、ありません？\r\nもう一人の自分に遭っちゃうって話…\r\nチサト：気になっちゃうの？\r\nユカリ：うん…', 'パッケージ', '2018-03-24 10:17:50', '2018-05-29 22:36:49'),
(69, 'ムーンライトシンドローム', 'むーんらいとしんどろーむ', 'むーんらいとしんどろーむ', 7, 272, '', 11, 3, 143, '高層マンションが立ち並ぶ新興都市・雛代町。\r\n急激に変貌を遂げたこの街で、女子高生ミカに怪しい影が忍び寄る。\r\n執拗につきまとうストーカー、耳を犯す妄想電波、高層団地からの集団飛び降り自殺。\r\n10篇のオムニバスストーリーが、あなたを狂気の世界へ誘う……。', 'パッケージ', '2018-03-24 10:17:50', '2018-05-29 22:36:50'),
(70, 'トワイライトシンドローム～再会～', 'とわいらいとしんどろーむさいかい', 'とわいらいとしんどろーむ30', 4, 195, '', 11, 4, 284, '全ての始まりは、七十年以上も前。\r\nある悲惨な出来事をきっかけに一帯は買収され、森を切る取るかのように私立秀栄学園は建設された。\r\nそして今また、七十年の時を経て、私立秀栄学園に通うユウリ、マサ、アツシによって危険な火蓋が切られた。\r\n\r\n晩夏、この街は夕闇を境に、忘れ去られていた過去をのぞかせることになった。', '<a href=\"https://ja.wikipedia.org/wiki/%E3%83%88%E3%83%AF%E3%82%A4%E3%83%A9%E3%82%A4%E3%83%88%E3%82%B7%E3%83%B3%E3%83%89%E3%83%AD%E3%83%BC%E3%83%A0\" target=\"_blank\"><cite title=\"Wikipedia 2017年2月14日 (火) 06:34更新版\">Wikipedia</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:49'),
(71, 'トワイライトシンドローム禁じられた都市伝説', 'とわいらいとしんどろーむきんじられたとしでんせつ', 'とわいらいとしんどろーむ40', 4, 196, '', 11, 5, 287, '謎のチェーンメールが桐塚高校の生徒の中で出回っていた…\r\nミズキが転校して早々のある日、クラスメイト(カナとメグミ)が疾走するという事件が起こる。\r\n―彼女たちは「ナナシ」からのチェーンメールに記された都市伝説を再現して神隠しにあったのではないか― クラスでは朝から、そんな噂でもちきりだった。\r\n\r\nそんな中、「オクジョウ　デ　マツ」ナナシから直接メールを受け取ったミズキは、屋上へ行く。\r\nそこで同じく「ナナシ」からメールを受け取ったというリコに出会う。\r\n屋上で失踪したメグミの携帯電話を拾った二人は、事件の晩にメグミが最後に電話したレイカに話を聞きに行く。\r\n\r\nその日をきっかけに、ミズキ、リコ、レイカの3人は「ナナシ」からのメールをもとに事件の真相へと迫っていく。', '<a href=\"http://www.twilight-syndrome.jp/game/\" target=\"_blank\">Wikipedia</a> ※短縮のため、一部改変', '2018-03-24 10:17:50', '2018-05-29 22:36:49'),
(72, 'ダークメサイア', 'だーくめさいあ', 'だーくめさいあ', 4, 158, '', NULL, NULL, 218, '東京の地下鉄で事故が発生し、電車に乗っていた主人公の目の前に謎の生命体が突如現れる。\r\n乗り合わせていた女子高生の杉浦奈緒美と一緒に逃げるうちに、2人は「東京メッシュ」と呼ばれる地下迷路に迷い込む。\r\n追いかけてくる影。\r\nできることは、ただ逃げることだけ。', '<a href=\"https://ja.wikipedia.org/wiki/%E3%83%80%E3%83%BC%E3%82%AF%E3%83%A1%E3%82%B5%E3%82%A4%E3%82%A2\" target=\"_blank\"><cite title=\"Wikipedia 2018年3月3日 (土) 08:41更新版\">Wikipedia</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:49'),
(73, 'ハングリィ・ゴースト', 'はんぐりぃごーすと', 'はんぐりぃごーすと', 6, 235, '', NULL, NULL, 230, '入るたびにその形を変える、体感アトラクション。\r\nその中で、分身キャラクターを操るのではなく、自分自身が行動するという感覚。\r\nこのアトラクションの中にはストーリーもルールも無い。\r\nプレーヤーの起こす行動すべてが、ストーリーとなり、ルールとなる。\r\n何を行うのも、何を行わないのも、全てプレーヤーの自由。\r\n但し、全ての行動は、このアトラクションの中身に何かしらの変化を与える。\r\nそれは、出口までの道のりに。\r\n道中に起こる事件に。\r\nそして、出口で渡される、行動心理診断書の内容に。', '<a href=\"http://www.jp.playstation.com/software/title/scps15046.html\" target=\"_blank\"><cite title=\"プレイステーションオフィシャルサイト\">PSソフトウェアカタログ</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:50'),
(74, 'プリズナー オブ アイス', 'ぷりずなーおぶあいす', 'ぷりずなーおぶあいす', 6, 259, '', NULL, NULL, 153, '1937年、イギリス軍による｢ポラリス作戦｣により南極大陸で発見された、凍りつけの「プリズナー・オブ・アイス」と呼ばれる怪物。\r\nこの怪物の兵器利用を企むナチス・ドイツ軍による攻撃が始まった。\r\nアメリカ軍より依頼を受け主人公のアメリカ軍諜報部のライアン大尉は｢ポラリス作戦｣に参加し、潜水艦ヴィクトリア号に乗り込み南極へ向かい、基地でナチスや怪物と戦う。', '<a href=\"https://ja.wikipedia.org/wiki/%E3%83%97%E3%83%AA%E3%82%BA%E3%83%8A%E3%83%BC%E3%83%BB%E3%82%AA%E3%83%96%E3%83%BB%E3%82%A2%E3%82%A4%E3%82%B9\" target=\"_blank\"><cite title=\"Wikipedia 2015年4月30日 (木) 13:35 更新版\">Wikipedia</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:37:28'),
(76, '厄-友情談疑-', 'やくゆうじょうだんぎ', 'やく01', 8, 275, '', 36, 1, 141, '小学校の廃屋に集まった友達の迎える恐怖の体験。\r\n独特のビジュアル、あまりにも過激な展開がプレイヤーの常識をゆさぶる。\r\n･･･まだ間に合います･･･決して安易なプレイをお勧めしません！\r\nカルトゲームとして評価された「厄」･･･あなた感情を逆撫でする危険な作品です！', '<a href=\"http://www.jp.playstation.com/software/title/jp0031npjj00019_000000000000000001.html\" target=\"_blank\"><cite title=\"プレイステーションオフィシャルサイト\">PSソフトウェアカタログ</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:50'),
(77, '夕闇通り探検隊', 'ゆうやみどおりたんけんたい', 'ゆうやみどおりたんけんたい', 8, 286, '', NULL, NULL, 128, '「人間ガラス」の噂は、果たして本当なのか？\r\nそれを見たものは「100日以内に死ぬ」という。\r\nその「呪い」から逃れるため、学校で渦巻く「噂」をヒントに3人+1匹の探検は始まる。(日毎に増していく霊障レベル)。\r\nその背後では、少年少女たちの微妙な心模様が織りなすもう一つのドラマが展開。\r\n3者3様の視点で「霊」の存在を探っていく「キャラクタースイッチ」によって、日常ありふれた(超リアルに演出された)陽見市は多面的に構成され、プレーヤーに迫る。\r\nあなたの日常にも、こんな出来事が起こるかもしれない?!', 'パッケージ', '2018-03-24 10:17:50', '2018-05-29 22:36:50'),
(78, 'LAST WHISPER', 'らすとうぃすぱー', 'らすとうぃすぱー', 9, 290, '', NULL, NULL, 125, '生の終焉を迎えた者たちが、残す最後の囁き。\r\nその悲しみの声に導かれ、男は自分の心にある楔を引き抜くため、女刑事は本当の自分を探して、呪われた運命に立ち向かう。\r\n失踪した殺人犯の少女「リサ」は何処へ?死者の織り成す矛盾の迷宮。\r\n繰り返される超自然犯罪。\r\n点在する謎が呪いによって一つに結ばれたとき、死者達が恐怖の真実を語りだす。', '<a href=\"https://game.watch.impress.co.jp/docs/review/478793.html\" target=\"_blank\"><cite title=\"Amazon\">Amazon</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:48'),
(79, '流行り神 警視庁怪異事件ファイル', 'はやりがみけいしちょうかいいじけんふぁいる', 'はやりが10', 6, 229, '', 12, 1, 58, '都市伝説―それは、あたかも実際に起こったかのように流布される噂話。\r\n首なしライダー、テケテケ、ピアスの白い糸、赤マント……。\r\n『流行り神』は、現代社会に突如として現れ、瞬く間に広がっていったこれら都市伝説を主な題材としています。\r\nプレイヤーは警視庁の刑事となって、身の回りに起こる様々な事件を、『科学的』に解明するか、『オカルト的』アプローチで捉えるかの選択に迫られます。\r\nこの選択によって、物語の方向が大きく変化していきます。', '<a href=\"http://hayarigami.com/1/\" target=\"_blank\">公式サイト</a>', '2018-03-24 10:17:50', '2018-05-29 22:36:50'),
(80, '流行り神2 警視庁怪異事件ファイル', 'はやりがみ2けいしちょうかいいじけんふぁいる', 'はやりがみ20', 6, 230, '', 12, 2, 63, '真偽が明らかではない、怪奇で魅力的な噂話――都市伝説。\r\n“友達の友達”という希薄な間柄を介して流布されていく、嘘とも真とも取りにくいこれら不可解な話は、現在でもその多くが語り告がれています。\r\nこっくりさん、赤マント、テケテケ、ピアスの白い糸など、有名無名問わず枚挙にいとまがありません。\r\n『流行り神』シリーズはこれら都市伝説が色濃く絡んだ怪奇事件を、科学的かオカルト的のいずれかの見地に立って追っていく選択肢型アドベンチャーゲームです。\r\n選択によって話の展開や結末が変化していきます。', '<a href=\"http://hayarigami.com/2/\" target=\"_blank\">公式サイト</a>', '2018-03-24 10:17:50', '2018-05-29 22:36:50'),
(81, '流行り神3 警視庁怪異事件ファイル', 'はやりがみ3けいしちょうかいいふぁいる', 'はやりがみ30', 6, 231, '', 12, 3, 4, '誰が最初に間違ったのか？ 誰が正しく、誰が悪いのか？\r\nどこからが罪に問われ、どこからが犯罪ではないのか？\r\n嘘か真かもさだかではない奇妙な話―――都市伝説。\r\n本作は、そんな都市伝説が題材の怪異事件に挑み、その真相を“科学的見地”もしくは“オカルト的見地”に立って究明していく選択肢型、都市伝説推理アドベンチャーです。\r\nどちらの視点から捜査を行い、隠された事件の真相に辿り着くのか？\r\n全てはあなたの推理次第です―――', '<a href=\"http://nippon1.jp/titlelineup/index.php?mode=detail&no=72\" target=\"_blank\">公式サイト</a>', '2018-03-24 10:17:50', '2018-05-29 22:36:50'),
(82, '彼岸島', 'ひがんじま', 'ひがんじま', 6, 241, '', NULL, NULL, 234, 'いつ吸血鬼に襲われるか分からない張り詰めた緊張感。\r\n恐ろしい異形の者達とのスリリングな戦闘シーン。\r\n先がまったく読めないストーリー展開。物語の裏に隠された数多くの謎…。\r\n多くのファンを虜にしている魅力はそのままに、ゲームは原作とは違う、オリジナルストーリーで展開。\r\n君は狂気に満ちた島から「生きて還る」ことができるか…!', '<a href=\"http://www.comshop.co.jp/g-haku/index.php?jan=4582104070081\" target=\"_blank\"><cite title=\"ゲーム博物館\">パッケージ</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:50'),
(83, 'DEMENTO', 'でめんと', 'でめんと', 4, 185, '', NULL, NULL, 225, '暗鬱な地下室――冷たい織りの中で目を覚ますフィオナ。\r\n見も知らぬ古城、それは、悪意をはらんで歪んでいた。\r\n城の住人達はフィオナの問いかけに答えることもなく、彼女は困惑の中に取り残される。\r\n偶然に救い出した犬、ヒューイをパートナーに、フィオナの探索が始まる。\r\n狂気に彩られた悪夢は、彼女をどこへ導くのか。', '<a href=\"http://www.comshop.co.jp/g-haku/index.php?jan=4976219652377\" target=\"_blank\"><cite title=\"ゲーム博物館\">パッケージ</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:49'),
(84, 'ファミコン探偵倶楽部 消えた後継者', 'ふぁみこんたんていくらぶきえたこうけいしゃ', 'ふぁみこんたんていくらぶ10', 6, 250, '', 13, 1, 326, '崖の傍で倒れていた主人公は、「天地」という男性の声で目を覚まし、介抱を受け意識を取り戻す。\r\nその後、自らが倒れていた現場で出会った少女「橘あゆみ」から、自分が「空木探偵事務所に所属する探偵助手」であることを思い出す。\r\n\r\nそして、うらびれた寒村、明神村一の資産家である綾城家から『突然死を遂げた当主「綾城キク」の死』について調査を依頼されていたことを知らされた主人公は、自分の名前を思い出した後「明神村」にある「綾城家」に向かう。', '<a href=\"https://ja.wikipedia.org/wiki/%E3%83%95%E3%82%A1%E3%83%9F%E3%82%B3%E3%83%B3%E6%8E%A2%E5%81%B5%E5%80%B6%E6%A5%BD%E9%83%A8_%E6%B6%88%E3%81%88%E3%81%9F%E5%BE%8C%E7%B6%99%E8%80%85\" target=\"_blank\"><cite title=\"Wikipedia 2018年3月27日 (火) 15:51 更新版\">Wikipedia</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:48'),
(85, 'ファミコン探偵倶楽部PartII うしろに立つ少女', 'ふぁみこんたんていくらぶ2うしろにたつしょうじょ', 'ふぁみこんたんていくらぶ20', 6, 251, '', 13, 2, 327, 'ふとしたきっかけから私立探偵　空木俊介と出会い、彼の助手として探偵の道を歩み始めたあなたは、ある少女の殺人事件を担当する。 \r\n\r\n少女の名は小島洋子。\r\n丑美津（うしみつ）高校１年の女生徒だった。\r\n洋子の親友・橘あゆみの協力の下、あなたは学校に伝わる「うしろの少女」という、不気味なうわさ話にたどり着く。\r\nどこにでもある、ありふれた学校の怪談が彼女の死とどんな関係があるのか？\r\n\r\n一つの殺人事件はさらに謎を深めていく…', '<a href=\"https://www.nintendo.co.jp/n02/shvc/btcj/\" target=\"_blank\"><cite title=\"ファミコン探偵倶楽部2のページ\">公式サイト</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:48'),
(86, 'BS探偵倶楽部 雪に消えた過去', 'びーえすたんていくらぶゆきにきえたかこ', 'びーえすたんていくらぶゆきにきえたかこ', 6, 239, '', 13, 3, 322, '実家で病気療養中の母・敏江に会うために、橘あゆみは母の実家のある尾地谷村へ連休を利用し帰省する。\r\n久し振りの母との再会に旧友の令子も加わり、ゆっくりとくつろぐあゆみ達。\r\n\r\nしかし、翌朝、尾地谷村の前村長・草野剛三が、自宅の離れで殺害されているのが発見される。\r\n死体を発見したのが敏江で、現場には彼女以外の足跡が見つからなかったことから、敏江は警察で事情聴取を受ける。\r\n母が警察署から帰ってくるまでの間に、事情を聞いて回ったあゆみは「橘家」と「草野家」の間にある確執を知る事になる。\r\n18年前の事件、村に伝わる落武者伝説、そして今回の事件。\r\n\r\n村で様々な噂が飛び交う中、次の事件が発生する…。', '<a href=\"https://ja.wikipedia.org/wiki/BS%E6%8E%A2%E5%81%B5%E5%80%B6%E6%A5%BD%E9%83%A8_%E9%9B%AA%E3%81%AB%E6%B6%88%E3%81%88%E3%81%9F%E9%81%8E%E5%8E%BB\" target=\"_blank\"><cite title=\"Wikipedia 2017年12月10日 (日) 05:24 更新版\">Wikipedia</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:48'),
(87, 'アカイイト', 'あかいいと', 'あかいいと10', 1, 3, '', 37, 1, 21, 'この夏に唯一の肉親だった母親を亡くし、遺産の整理をしていた桂は、父親が経観塚（へみづか）という聞き慣れぬ土地に、歴史のある日本屋敷を持っていたことを知る。\r\n桂は処分するかどうかを決めるために、現物を見に経観塚を訪れることに。\r\nその道中の列車の中で、天を貫くほどの巨大な木と、蝶の群れを従えた少女・ユメイが現れる不思議な夢を見る……。\r\n\r\n経観塚に伝わる山神と人柱の伝承とは？\r\n失われた桂の記憶の真実は？\r\n桂の生まれ故郷でもある経観塚。\r\n舞台に役者が揃うとき、運命の輪が静かに廻り出す……。', '<a href=\"https://ja.wikipedia.org/wiki/%E3%82%A2%E3%82%AB%E3%82%A4%E3%82%A4%E3%83%88\" target=\"_blank\"><cite title=\"Wikipedia 2018年3月17日 (土) 05:27更新版\">Wikipedia</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:47'),
(88, 'アオイシロ', 'あおいしろ', 'あかいいと20', 1, 4, '', 37, 2, 17, '青城女学院の剣道部の部長を務める少女・小山内梢子は、剣道部の合宿のために遥か南の地である卯奈咲（うなさか）は咲森寺（しょうしんじ）へとやって来た。\r\nその日の夜、梢子がマネージャーの相沢保美と共に浜辺を歩いていた所、1人の少女が浜辺に倒れているのを目撃する。\r\n梢子に助けられた少女はナミと名付けられ、以後彼女は梢子と保美に懐くようになる。\r\nその時を境に梢子の運命の歯車は大きく回り出す事になる……。', '<a href=\"https://ja.wikipedia.org/wiki/%E3%82%A2%E3%82%AA%E3%82%A4%E3%82%B7%E3%83%AD\" target=\"_blank\"><cite title=\"Wikipedia 2018年3月17日 (土) 05:2更新版\">Wikipedia</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:47'),
(89, 'ダブルキャスト', 'だぶるきゃすと', 'だぶるきゃすと', 4, 162, '', NULL, NULL, 221, '夏休みも直前に控えたある日、大学で所属する映画研究部の飲み会で酔いつぶれ、帰る途中生き倒れてしまった主人公。\r\nその時介抱してくれた記憶喪失の謎の美少女・赤坂美月となしくずし的に主人公の家で同棲することになる。\r\nやがて、映画研究部でいわく付きのシナリオ「かこひめの寝屋」を撮ることになり、そのヒロインに美月が抜擢される。\r\nしかし、その映画を撮り始めようとしたころから、主人公と美月のまわりで奇妙な出来事が起き始めていた・・・', '<a href=\"http://dic.nicovideo.jp/a/%E3%83%80%E3%83%96%E3%83%AB%E3%82%AD%E3%83%A3%E3%82%B9%E3%83%88\" target=\"_blank\"><cite title=\"ニコニコ大百科(仮)\">ニコニコ大百科(仮)</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:47'),
(92, '四八（仮）', 'しじゅうはち', 'しじゅうはち', 3, 123, '', NULL, NULL, 201, '北は北海道から南は沖縄まで、日本全国250箇所以上で、実際にスタッフが取材と撮影を観光！\r\n集められた民間伝承や都市伝説の、怖い話・悲しい話・不思議な話などを、DVD-ROM容量の眼界まで収録！\r\nしかも、登場人物はすべて実写で、究極のリアリティを追求した！\r\n「学校であった怖い話」の飯島多紀哉がおくる、新たな本格Jホラー・ミステリー大作！', '<a href=\"http://www.comshop.co.jp/g-haku/index.php?jan=4983164735734\" target=\"_blank\"><cite title=\"ゲーム博物館\">パッケージ</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:49'),
(93, 'ナナシノゲエム', 'ななしのげえむ', 'ななしのげえむ10', 5, 200, '', 14, 1, 228, 'プレイした者は一週間以内に死を迎えるという謎のゲエム。\r\nある日突然、そのゲエムが主人公の「TS」に配信されてくる。\r\n配信主である大学の先輩はゲエムに熱中するあまり、自宅に引きこもってしまっているという。\r\n主人公は友人に頼まれて先輩の住んでいるマンションを訪れるが、そこには想像を絶する恐ろしい出来事が待ち構えていた…', '<a href=\"http://dic.nicovideo.jp/a/%E3%83%8A%E3%83%8A%E3%82%B7%E3%83%8E%E3%82%B2%E3%82%A8%E3%83%A0\" target=\"_blank\">ニコニコ大百科(仮)</a>', '2018-03-24 10:17:50', '2018-05-29 22:36:49'),
(94, 'ナナシノゲエム目', 'ななしのげえむめ', 'ななしのげえむ20', 5, 201, '', 14, 2, 5, 'かつて世間を騒がせた「呪いのゲーム」の噂。\r\nクリアできなければ一週間以内に死ぬというそのゲームをプレイしていた主人公の親友は一週間後、謎の死を迎えた。\r\n驚く間もなく主人公のもとに「呪いのゲーム」が配信されてくる。\r\n呪いの真相を暴くため半信半疑でプレイし始めた主人公に、信じられない現象が次々と襲いかかり…', '<a href=\"http://dic.nicovideo.jp/a/%E3%83%8A%E3%83%8A%E3%82%B7%E3%83%8E%E3%82%B2%E3%82%A8%E3%83%A0%E7%9B%AE\" target=\"_blank\">ニコニコ大百科(仮)</a>', '2018-03-24 10:17:50', '2018-05-29 22:37:29'),
(95, 'ファーレンハイト', 'ふぁーれんはいと', 'ふぁーれんはいと', 6, 249, '', NULL, NULL, 155, '2009年、真冬のニューヨーク。\r\n\r\n主人公のルーカス・ケインはダイニングレストランのトイレにて、まるで誰かに身体を操られているかのように、自分の意思とは無関係に殺人を犯してしまう。\r\n正気を取り戻した時には見知らぬ死体が目の前に横たわり、動揺を抑えられないケインは死体を隠し、早々に逃亡を図る。\r\nまもなく死体は発見され、ニューヨーク警察の捜査官であるカーラとタイラーの二人が事件の真相を解き明かすべく、僅かな手がかりを頼りにルーカスを追い詰めていくが…。', '<a href=\"https://ja.wikipedia.org/wiki/F.E.A.R._(%E3%82%B2%E3%83%BC%E3%83%A0%E3%82%BD%E3%83%95%E3%83%88)\" target=\"_blank\"><cite title=\"Wikipedia 2017年9月17日 (日) 17:33更新版\">Wikipedia</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:50'),
(96, 'DEMENTIUM-閉鎖病棟-', 'でぃめんてぃあむへいさびょうとう', 'でぃめんてぃあむへいさびょうとう', 4, 174, '', NULL, NULL, 237, '廃墟と化した閉鎖病棟。\r\n暗闇で次々に起こる惨劇。\r\n何故自分がここにいるのか、自分は誰なのか……\r\n全ての謎を解き明かした時、待っているのは……', 'パッケージ', '2018-03-24 10:17:50', '2018-05-29 22:36:49'),
(97, 'THE廃屋病棟～呪われた病院からの脱出～', 'ざはいおくびょうとうのろわれたびょういんからのだっしゅつ', 'ざはいおくびょうとうのろわれたびょういんからのだっしゅつ', 3, 116, '', NULL, NULL, 193, '廃墟と化した連続殺人事件の舞台、『廃屋病棟』に足を踏み入れる。\r\nそこで体験する謎と恐怖。\r\n20年前に起きた連続殺人事件・・・\r\n4人の若者の不可解な死・・・\r\n2つの事件の接点を求めてジャーナリスト柏木玲は事件現場でもあるすでに廃墟と化した『坂の上総合病院』に足を踏み入れる。', '<a href=\"https://www.d3p.co.jp/haioku/\" target=\"_blank\">公式サイト</a>', '2018-03-24 10:17:50', '2018-05-29 22:36:48'),
(98, '学校の怪談DS', 'がっこうのかいだんでぃーえす', 'がっこうのかいだんでぃーえす', 2, 55, '', NULL, NULL, 96, '主人公たちの仲良しグループは、夏休みの自由研究として自分たちの小学校に伝わる怪談を調べることに。\r\n\r\n夜の学校に集まる主人公たちだが、友達の一人が約束の時間になっても来ない。\r\nどうやら一人で学校に入って、そのまま消えてしまったらしい…。\r\n\r\n居なくなった友達を捜して、主人公は夜の学校の探検を始める…。', '<a href=\"http://www.compileheart.com/spec/?hard=ds&title=kaidan\" target=\"_blank\"><cite title=\"公式サイト\">公式サイト</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:48'),
(99, 'みてはいけない', 'みてはいけない', 'みてはいけない', 7, 271, '', NULL, NULL, 145, 'みてはいけない…\r\nでもみたい…　ついみてしまう…\r\n怖い…　でもやめられない…\r\n\r\n人の恐怖感情をすぐに呼び起こす「心霊写真」。\r\nその心霊写真の中から、心霊を見つけ出し、なぞることで除霊していく、そしてその度に何かが変化していく、新感覚心霊ゲーム。', '<a href=\"http://www.comshop.co.jp/g-haku/index.php?jan=4582246790236\" target=\"_blank\"><cite title=\"ゲーム博物館\">パッケージ</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:50'),
(100, '恐怖体感 呪怨', 'きょうふたいかんじゅおん', 'きょうふたいかんじゅおん', 2, 65, '', NULL, NULL, 111, '世界中を恐怖に陥れた、ジャパニーズホラーの金字塔がついにWiiに登場…。\r\nプレイヤーは、Wiiリモコンを懐中電灯に見たて廃墟や空間をさ迷い歩く。そこには、深く、暗い怨念に囚われたあの親子の姿が…。\r\n難しい操作や、謎解きは一切不要。まるでその場所にいるかのような、臨場感や没入感で、純粋な恐怖を体感することができます。', '<a href=\"https://www.nintendo.co.jp/wii/software/rjoj/\" target=\"_blank\"><cite title=\"任天堂\">Wiiソフトウェア</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:37:29'),
(101, 'CALLING～黒き着信～', 'こーりんぐくろくちゃくしん', 'こーりんぐくろくちゃくしん', 2, 87, '', NULL, NULL, 7, '巷でまことしやかに噂されているサイト……、\"黒のページ\"\r\n普段、真っ黒な背景にカウンターしかないそのサイトは、時折チャットルームに繋がるという。\r\nそして、チャットルームに入った者は、深淵からの\"着信\"を受け、生と死の狭間に取り込まれ、永遠に彷徨い続けると言われている。\r\n最近、原因不明の昏睡状態の末、死亡するという事件が続いているが、それらは魂が\"黒のページ\"に取り込まれてしまったのではないかと囁かれている。\r\n今晩も、噂に招き寄せられた者がチャットに集っていた。', '<a href=\"https://www.nintendo.co.jp/wii/software/scaj/\" target=\"_blank\"><cite title=\"任天堂\">Wiiソフトウェア</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:48'),
(102, 'アリス・イン・ナイトメア', 'ありすいんないとめあ', 'ありすいんないとめあ', 1, 14, '', 41, 1, 28, '誰もが知っている、不思議で、恐ろしい物語。\r\nアリスは、かつて冒険の日々を過ごした不思議の国を再び訪れました。\r\n草木は枯れ、殺気と混沌に満ち、腐乱した死体の匂いが漂う――\r\nもうそこは、あのワクワクするような世界ではありません。\r\n血の付いたナイフをにぎりしめ、アリスは小さな決心をします。\r\nもう一度、美しい不思議の国を取り戻そう――と。', 'パッケージ', '2018-03-24 10:17:50', '2018-05-29 22:36:47'),
(103, 'Alice: Madness Returns', 'ありすまっどねすりたーんず', 'ありすまっどねすりたーんず', 1, 15, '', 41, 2, 429, 'ここじゃあ、誰もが狂ってる。\r\n\r\nすべてが変わってしまった不思議の国に、再び足を踏み入れたアリス。\r\n今もなお彼女の心は、憂鬱な幻覚と、過去の記憶に怯えています。\r\n輝いていた世界―そして自分自身―を取り戻せるでしょうか。', 'パッケージ', '2018-03-24 10:17:50', '2018-05-29 22:36:47'),
(104, 'イルブリード', 'いるぶりーど', 'いるぶりーど', 1, 29, '', NULL, NULL, 75, '挑戦者を地獄の修羅場に叩き落とすバーチャルホラーランド本日オープン！', 'パッケージ', '2018-03-24 10:17:50', '2018-05-29 22:36:48'),
(105, 'CARRIER', 'きゃりあ', 'きゃりあ', 2, 62, '', NULL, NULL, 107, '突如消息を絶った巨大空母ヘイムダル。\r\n調査に向かった主人公　ジャック＝イーグルフェザーが見たものは、植物に侵され変わり果てた艦内と乗組員達だった。\r\n逃げ場のない閉鎖空間での壮絶な戦いが今始まる！！', '<a href=\"http://tsutaya.tsite.jp/item/game/PTA0000FDB3F\" target=\"_blank\"><cite title=\"TSUTAYA\">TSUTAYA</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:48'),
(107, 'ゴーストトラップ', 'ごーすととらっぷ', 'ごーすととらっぷ', 2, 82, '', NULL, NULL, 164, 'ゴーストの存在を信じない幼馴染・アリシアと口論になったゴーストハンター・ジョージは、彼女にゴーストの存在を信じさせるために、幽霊屋敷として有名なアリギエリ邸へ連れて行くが、そこで閉じ込められてしまう。', '<a href=\"https://ja.wikipedia.org/wiki/%E3%82%B4%E3%83%BC%E3%82%B9%E3%83%88%E3%83%88%E3%83%A9%E3%83%83%E3%83%97\" target=\"_blank\"><cite title=\"Wikipedia 2016年11月23日 (水) 04:55 更新版\">Wikipedia</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:48'),
(108, 'コワイシャシン～心霊写真奇譚～', 'こわいしゃしんしんれいしゃしんきたん', 'こわいしゃしんしんれいしゃしんきたん', 2, 92, '', NULL, NULL, 160, '霊能力を持つ少女、緋織。\r\n彼女は不気味な心霊写真を除霊することを生業としている。\r\nなぜ緋織にはこのような力があるのか？\r\n彼女が行う除霊の本当の意味は何か？\r\nステージを進めることによって謎が明らかになってゆく・・・。', 'パッケージ', '2018-03-24 10:17:50', '2018-05-29 22:36:47'),
(109, 'サイレントヒル', 'さいれんとひる', 'さいれんとひる10', 3, 101, '', 16, 1, 172, '一人娘のシェリルを連れて、休暇先に出かけた主人公ハリー・メイソン。\r\n夜道に突然、飛び出してきた人影に、過って事故を起こしてしまう。\r\n崖下に転落した車のなかで目覚めたとき、車内に娘の姿はなかった・・・\r\n\r\n目の前には深い霧に包まれ、不気味に静まり返った町がある。\r\n娘の身を案じたハリーは、彼女を捜しに霧に閉ざされた町、サイレントヒルへと足を踏み入れてゆく。\r\n\r\nそれがすべての始まりだったのか、あるいはあらかじめ決まっていたことなのか、彼には知る由もない・・・', '<a href=\"http://www.konami.jp/gs/game/vx131/index.html\" target=\"_blank\">公式サイト</a>', '2018-03-24 10:17:50', '2018-05-29 22:36:48'),
(110, 'サイレントヒル2', 'さいれんとひる2', 'さいれんとひる20', 3, 102, 'ホラーアドベンチャー', 16, 2, 173, '手紙が来た。\r\n封筒に記された名前はメアリー\r\nそれは3年前に病死した妻の名だった。\r\n\r\nジェイムスはサイレントヒルへとやってきた。\r\n湖畔に眠る静かなリゾートタウン、霧に包まれた街に。\r\n死んだ人間から手紙など来るはずがない、それをわかっていても。\r\n\r\n「それでも私はメアリーに会いに来た」\r\n\r\n真偽を確かめたいのか、奇跡を信じているのか、それは彼自身にもわからない。', '<a href=\"http://www.konami.jp/gs/game/sh2/index.html\" target=\"_blank\">PS2版公式サイト</a>', '2018-03-24 10:17:50', '2018-05-29 22:36:48'),
(111, 'サイレントヒル3', 'さいれんとひる3', 'さいれんとひる30', 3, 103, 'ホラーアドベンチャー', 16, 3, 180, '不意に、何の前触れも、理由もわからぬままに、それは訪れる。\r\n悪夢に浸食されたように世界は変貌し、ヘザーはその中に取り込まれてしまう。\r\n休日にショッピングモールを満たしていた幸福なざわめきは、陰温な沈黙に変わり、 聞こえてくるのは異形の者の足音だけ。\r\nヘザーには、逃げ出すことしかできなかった。\r\n生きるため・・・、 襲い掛かって来る怪物を倒すために、拳銃を握り締めて・・・。', '<a href=\"http://www.konami.jp/products/silenthill_hd_ps3/\" target=\"_blank\">PS3版公式サイト</a>', '2018-03-24 10:17:50', '2018-05-29 22:36:48'),
(112, 'サイレントヒル4 -THE ROOM-', 'さいれんとひる4ざるーむ', 'さいれんとひる40', 3, 104, '', 16, 4, 183, '2年前からあるアパートの302号室で生活をしている主人公ヘンリー・タウンゼント。\r\n彼の部屋に5日前から異変が起き始めた。\r\n毎晩同じ夢を見るようになり、そして―――――部屋から出られなくなった…\r\nテレビも、電話も、大声で叫んでも外には通じない。\r\n外れない玄関の鎖、割れない窓、この302号室だけ日常から完全に隔離されたのだ。\r\nそんな中バスルームの壁に大きな穴が…。\r\n5日間302号室に閉じ込められていたヘンリーは、その穴に入ることはためらいもしなかった。\r\nこれから表現しきれないほどの地獄が待っている事も知らずに…', '<a href=\"http://www.comshop.co.jp/g-haku/index.php?jan=4542084001024\" target=\"_blank\"><cite title=\"ゲーム博物館\">パッケージ</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:48'),
(113, 'サイレントヒル ゼロ', 'さいれんとひるぜろ', 'さいれんとひる41', 3, 105, '', 16, 5, 185, 'トラック運転手であるトラビス。夜中トラックを運転していると、突然少女の姿が…\r\n彼はその少女の導くままに\"SILENT HILL\"へと足を踏み入れてしまう…\r\n霧に包まれた街\"SILENT HILL\"で彼は次々に奇妙な出来事に見舞われる。\r\n度々訪れる自身の記憶に悩まされながら、次々に現れるクリーチャーを打ち倒し、この町の謎を解いていくトラビス。\r\n彼は表の世界に戻って来られるのだろうか…', '<a href=\"http://www.comshop.co.jp/g-haku/index.php?jan=4988602138610\" target=\"_blank\"><cite title=\"ゲーム博物館\">パッケージ</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:48'),
(114, 'Silent Hill: Homecoming', 'さいれんとひるほーむかみんぐ', 'さいれんとひる42', 3, 106, '', 16, 6, 187, '突如姿を消した弟の消息を探りに、故郷のシェパードグレンに戻って来たアレックス・シェパードの呪われた物語。\r\nシェパードグレンからサイレントヒルへと伸びる霧に包まれた道・・・\r\nアレックスは弟の姿を追って漆黒の恐怖へと直面して行く。\r\n理性に必死にしがみ付きながらも、アレックスは彼の見る悪夢の謎、弟の失踪、父親の蒸発、\r\nそして彼自身の血と肉に染み付いた呪いを暴かなければならないのだ。 ', '<a href=\"http://www.konami.jp/products/e3_2008_silenthill/\" target=\"_blank\">公式サイト</a>', '2018-03-24 10:17:50', '2018-05-29 22:36:48'),
(115, 'the Ring', 'りんぐ', 'りんぐ', 9, 299, '', NULL, NULL, 197, 'リングの恐怖はまだ終わらない…\r\n最後の謎はゲームで明かされる', 'パッケージ', '2018-03-24 10:17:50', '2018-05-29 22:37:29'),
(117, 'DINO CRISIS', 'でぃのくらいしす', 'でぃのくらいしす', 4, 173, '', NULL, NULL, 288, '3年前。一人の科学者が実験中の事故で死亡した。\r\n彼が研究していたのは《完全なクリーンエネルギー＝“サードエナジー”》。\r\n実現性に乏しく、政府からの資金援助を絶たれた矢先の、不慮の事故だった。\r\nそして今。\r\n南海の小国に派遣されていたエージェントから意外な情報がもたらされた。\r\n(この国の軍事研究施設において、3年前に死亡したはずの科学者が、サードエナジーの研究活動を再開している)\r\n政府直属のスパイチームの一員であるレジーナは、科学者奪還の任務を受け、その軍事施設がある孤島へと向かう。\r\n彼らにとってそれは、いつも通りの“仕事”のひとつに過ぎないはずだった…。', '<a href=\"http://www.capcom.co.jp/product/detail.php?id=19\" target=\"_blank\">DL版公式サイト</a>', '2018-03-24 10:17:50', '2018-05-29 22:37:28'),
(118, 'DEEP FEAR', 'でぃーぷふぃあー', 'でぃーぷふぃあー', 4, 171, '', NULL, NULL, 227, '21世紀初頭のある日―。\r\n大気圏外より謎の物体が接近。\r\nそのまま太平洋上に落下した…。\r\nマリアナ海溝淵、海面下300mに作られた米軍潜水艦補給基地\"ビッグテーブル\"。\r\n長期の極秘任務を終え、補給中の原子力潜水艦が突如旋回し、極秘セクター\"NAVY\"エリアに激突、双方とも連絡が取れなくなってしまう。\r\n事態を重く見た上層部は、最優先事項として、民間レスキュー会社「ERS」にNAVYエリアに取り残された\"ワイズバーグ博士\"の救助を依頼した\r\n赴任して間もないERSの新人チーフ\"ジョン＝メイヤー\"は至急NAVYエリアへ救助に向かった。\r\nしかしそこには人間はいなかった。\r\n人間と呼べる姿は、もう…。', 'パッケージ', '2018-03-24 10:17:50', '2018-05-29 22:36:49'),
(119, 'ノクターン', 'のくたーん', 'のくたーん', 5, 207, '', NULL, NULL, 279, '1920年代後半から1930年代初頭、禁酒と大恐慌の時代。\r\nルーズベルト大統領がモンスターと戦うために作った「Spookhouse」と呼ばれる米国政府秘密組織のエージェントである主人公は、4つの奇妙な事件を調査し、狼男、ゾンビ、吸血鬼などのモンスターから人々を救う。', '<a href=\"http://gaming.wikia.com/wiki/Nocturne_(video_game)\" target=\"_blank\">FANDOM</a> ※意訳', '2018-03-24 10:17:50', '2018-05-29 22:36:49'),
(120, 'バンパイアハンターD', 'ばんぱいあはんたーでぃー', 'ばんぱいあはんたーでぃー', 6, 236, '', NULL, NULL, 273, '最終核戦争により人類の歴史に幕が降ろされて1万年後の未来。\r\n新たな覇者として、生き残った人類を隷属させ地上に君臨してきた貴族=バンパイアは原因不明の種族的衰退に陥り滅びの時を迎えようとしていた・・・\r\n「吸血鬼ハンター」は このような時代背景に登場した、貴族と対等に渡り合う事のできる賞金稼ぎ達だった。\r\n貴族、マイエルリンクに娘シャーロットを誘拐された村の富豪、ジョン・エルバーンは2組の吸血鬼ハンターに娘の救出を依頼する。\r\nそれぞれの特殊能力と、チームワークで辺境にその名をとどろかせるマーカス兄弟の5人、そして、ダンピールの”D”はそれぞれにマイエルリンクを追跡し、最終目的地、チェイテ城へとたどり着いた。', '<a href=\"http://www1.plala.or.jp/unknown/index.html\" target=\"_blank\"><cite title=\"D-Nix\">D-Nix</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:48'),
(121, '闇吹く夏 帝都物語ふたたび', 'やみぶくなつていとものがたりふたたび', 'やみぶくなつていとものがたりふたたび', 8, 283, '', NULL, NULL, 132, '世界的異常気象が東京にも押し寄せる1999年夏。\r\n主人公・木村は偶然拾ったプログラムを起動させたことから恐ろしい事件に巻き込まれる。\r\n狂い始めた東京の風水の謎とは!?\r\n「表」と「裏」の2つのステージを行き来しながら、その謎を解き明かし、風水を正すべく木村は立ち上がった・・・・。', 'パッケージ', '2018-03-24 10:17:50', '2018-05-29 22:36:50'),
(123, '遊星からの物体X episodeII', 'ゆうせいからのぶったいえっくすえぴそーど2', 'ゆうせいからのぶったいえっくす20', 8, 284, '', 18, 2, 129, '南極の米国科学調査隊からの連絡が途絶え、米陸軍救助隊が南極へと救助に向かう。破壊された基地を探索しているとテープレコーダーを発見。\r\n再生してみると謎の生物が人間に同化する事が記録されており、録音者はRJ マクレディと名乗っていた。\r\nそして探索を進めていくとUFOのような物体を発見。\r\n更に人間に同化する謎の生物と遭遇、戦闘することになる。\r\nそこから主人公達のサバイバルが始まっていく。', '<a href=\"https://ja.wikipedia.org/wiki/%E9%81%8A%E6%98%9F%E3%81%8B%E3%82%89%E3%81%AE%E7%89%A9%E4%BD%93X_episodeII\" target=\"_blank\"><cite title=\"Wikipedia 2014年9月4日 (木) 09:02更新版\">Wikipedia</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:37:34'),
(124, 'ルイージマンション', 'るいーじまんしょん', 'るいーじまんしょん', 9, 302, '', NULL, NULL, 123, 'ある日、ルイージの元に届いた「大きなお屋敷をプレゼント」という通知。\r\nルイージはその家に行ったものの、想像していた豪華な屋敷とは似ても似つかない、不気味なお化け屋敷だった。\r\n先に屋敷に入って行方不明となっていたマリオを探すため、持参した懐中電灯とオヤマー博士にもらった「オバキューム」「ゲームボーイホラー」を頼りに屋敷の扉を開く。', '<a href=\"https://ja.wikipedia.org/wiki/%E3%83%AB%E3%82%A4%E3%83%BC%E3%82%B8%E3%83%9E%E3%83%B3%E3%82%B7%E3%83%A7%E3%83%B3\" target=\"_blank\"><cite title=\"Wikipedia 2018年3月20日 (火) 12:17 更新版\">Wikipedia</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:37:29'),
(125, 'ゴーストハンター', 'ごーすとはんたー', 'ごーすとはんたー', 2, 84, '', NULL, NULL, 159, '非業の死。鬱積した怨念。極限の憎悪。\r\nそれは、少女をバケモノに変える――。\r\n    \r\nデトロイト市警の新米警官ラザラス・ジョーンズは、ゴーストの存在など信じていなかった。\r\nしかし、それは昨日までの話だ……。\r\n市内の廃校を巡回中、ラザラスは恐るべき事件に巻き込まれる。\r\n世にも不気味な身の毛もよだつ事件に。', '<a href=\"http://www.comshop.co.jp/g-haku/index.php?jan=4938833006332\" target=\"_blank\"><cite title=\"ゲーム博物館\">パッケージ</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:48'),
(127, 'ルールオブローズ', 'るーるおぶろーず', 'るーるおぶろーず', 9, 301, 'サイコミステリー・アドベンチャー', NULL, NULL, 69, '1930年代イギリス。\r\nジェニファーが迷い込んだ子供たちの秩序に縛られた世界。\r\n自分たちの王国を守るため、子供たちの無邪気な想いが残酷な行動となって、ジェニファーに襲い掛かる。\r\n大人の常識が通用しない、まるでおとぎ話のような世界に何故ジェニファーは迷い込んでしまったのか…。\r\nいつまでも醒めない夢のような、果てしなく続く不条理な世界。\r\n時折感じる懐かしさ、誰か私を助けて…。', '<a href=\"http://www.comshop.co.jp/g-haku/index.php?jan=4948872150934\" target=\"_blank\"><cite title=\"ゲーム博物館\">パッケージ</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:37:29'),
(128, 'RUN LIKE HELL', 'らんらいくへる', 'らんらいくへる', 9, 297, '', NULL, NULL, 124, 'いつもの巡回から戻ると、そこはエイリアンに占領されていた。\r\n彼ができることはただひとつ、この悪夢から逃げ出すことだ・・・', '<a href=\"http://www.comshop.co.jp/g-haku/index.php?jan=4976219650380\" target=\"_blank\"><cite title=\"ゲーム博物館\">パッケージ</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:37:29'),
(129, 'THE ゾンビV.S.救急車', 'ざぞんびばーさすきゅうきゅうしゃ', 'ざぞんびばーさすきゅうきゅうしゃ', 3, 110, '', NULL, NULL, 188, 'あなたは新興都市サンライトシティに住む医大生の青年。\r\nある日友人のスージーと地下室で資料を探していると、突然の大地震が二人を襲った。\r\n民衆は瞬く間に死に絶え、街は壊滅的なダメージを受けたのだった…。\r\nだが、被害はそれだけではなかった。\r\n死に絶えたと思われた者達は、数時間後によみがえった。人の形をした異形の輩…そう生ける屍として…\r\n街に残されている生存者を見殺しには出来ない…。\r\nあなたは救急車に乗り込み、生存者を病院に収容すべく街の探索に出るのであった…。', '<a href=\"http://www.comshop.co.jp/g-haku/index.php?jan=4527823993723\" target=\"_blank\"><cite title=\"ゲーム博物館\">パッケージ</cite></a> ※短縮のため一部削減', '2018-03-24 10:17:50', '2018-05-29 22:36:48'),
(130, 'SAINTS 聖なる魔物', 'せいんつせいなるまもの', 'せいんつせいなるまもの', 3, 143, '', NULL, NULL, 209, '魔物世界のバランスが崩れ、人間世界の存続も危機を迎える。\r\n運命の糸が選んだ最後の希望が現代っ子ジェン。\r\n彼女を導くガーゴイルのスクリーと共に平和を取り戻す長い旅が始まる。\r\n２人の能力を使い分けて困難を乗り越えるにつれ、彼らの宿命と葛藤、過去や決意などのドラマが展開されていく。', '<a href=\"http://www.jp.playstation.com/software/title/scps15052.html\" target=\"_blank\"><cite title=\"プレイステーションオフィシャルサイト\">PSソフトウェアカタログ</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:49'),
(131, 'F.E.A.R.', 'ふぃあー', 'ふぃあー10', 6, 254, '', 19, 0, 154, 'アーマカム社が極秘に開発していたクローン兵士の「レプリカ部隊」。\r\n指揮官のテレパシーで制御される兵士達が、指揮官パクストン・フェッテルの暴走により反乱を起こした。\r\n少数精鋭の特殊部隊「First Encounter Assault Recon」通称『F.E.A.R.』が出動。\r\n1週間前に配属されたばかりの主人公ポイント・マンも現場に駆り出された。\r\n\r\nフェッテルの追跡中、時折姿を見せる赤い服を着た少女。\r\nそして、主人公達に襲いかかる裸の女。\r\n彼女達は何者なのか？\r\nフェッテルはなぜ暴走したのか？\r\n「オリジン計画」の真相に迫りながら、物語は進んでゆく。', '<a href=\"https://ja.wikipedia.org/wiki/%E3%83%95%E3%82%A1%E3%83%BC%E3%83%AC%E3%83%B3%E3%83%8F%E3%82%A4%E3%83%88_(2006%E5%B9%B4%E3%81%AE%E3%83%93%E3%83%87%E3%82%AA%E3%82%B2%E3%83%BC%E3%83%A0)\" target=\"_blank\"><cite title=\"Wikipedia 2018年3月7日 (水) 01:12更新版\">Wikipedia</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:37:29'),
(132, 'F.E.A.R.2 -Project Origin-', 'ふぃあー2ぷろじぇくとおりじん', 'ふぃあー20', 6, 255, '', 19, 0, 6, '目が覚めると病院施設。\r\nそこでベケットは謎の男からの無線により、軍が施設を爆破しようとしている事を知る。\r\n\r\nもう一つの計画とは・・・ \r\n想像を絶する能力と復讐への渇望を持った幻影少女アルマ・・・\r\nアルマによって次々と殺されていくDeadEchoの隊員たち。\r\nベケットはアルマの持つ不思議な能力と戦い、そして自分が持つ能力に気付かされ始める。\r\nそして、その先には想像を絶する真実が待ち受けていた・・・', '<a href=\"http://www.jp.playstation.com/software/title/bljm60160.html\" target=\"_blank\"><cite title=\"プレイステーションオフィシャルサイト\">PSソフトウェアカタログ</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:50'),
(133, 'CONDEMNED:PSYCHO CRIME', 'こんでむどさいこくらいむ', 'こんでむど10', 2, 93, '', 20, 0, 161, 'とある街で、鳥の死骸が大量に発生するという奇怪な現象が起こっていた。\r\nそれと同じくして、暴徒による犯罪が異常なまでに増加し始めていた……\r\nFBI捜査官イーサン・トーマスは自分が担当している連続殺人犯「マッチメーカー」による犯行現場の調査をしていた。\r\n現場検証をしている最中、同ビルに容疑者が潜伏していることに気づいたイーサンは後を追うも、容疑者に襲われ銃を奪われてしまう。\r\nそして容疑者はイーサンから奪った銃で捜査官2人を殺害し、イーサン自身も男に殴られ意識を失う。\r\n意識が戻るとそこは自宅で、ソファーに謎の男が腰掛けていた。\r\n男から警官殺しの濡れ衣を着せられたことを知ったイーサンは自身の潔白を証明するため、真犯人を追うことを決意する。', '<a href=\"https://ja.wikipedia.org/wiki/Condemned:_Criminal_Origins\" target=\"_blank\"><cite title=\"Wikipedia 2017年4月13日 (木) 13:47 更新版\">Wikipedia</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:48'),
(134, 'Condemned2:Bloodshot', 'こんでむど2ぶらっどしょっと', 'こんでむど20', 2, 94, '', 20, 2, 163, '無慈悲な連続殺人犯が街を恐怖に陥れてから1年。\r\n事態は悪化し、積み重なる死体の山・・・。\r\nイーサン・トーマスは、人々を脅かす闇に対抗する最後の砦となるか。', '<a href=\"https://www.gametdb.com/PS3/BLUS30115\" target=\"_blank\"><cite title=\"Game TDB\">パッケージ</cite></a> ※翻訳し、少し改変しました。', '2018-03-24 10:17:50', '2018-05-29 22:36:48'),
(135, 'RESISTANCE～人類没落の日～', 'れじすたんすじんるいぼつらくのひ', 'れじすたんす10', 9, 305, '', 21, 1, 54, '1930年代、ある生科学実験レポートがロシアから漏れ始めた。\r\nそこには村々が一夜で全滅し、更には町ごと失われたという報告が記載されていた。\r\n人々はロシアの兵器開発と考え恐れたが、真実は謎の生物“キメラ”によるものだった…。\r\nそして1949年、“キメラ”は全世界に攻撃をし始める。\r\n数々の大国が侵略されていく中、アメリカ合衆国陸軍に属する勇敢な兵士“ネイサン・ヘイル”はキメラに立ち向かう。', '<a href=\"https://ja.wikipedia.org/wiki/RESISTANCE%E3%80%9C%E4%BA%BA%E9%A1%9E%E6%B2%A1%E8%90%BD%E3%81%AE%E6%97%A5%E3%80%9C\" target=\"_blank\"><cite title=\"Wikipedia 2018年3月17日 (土) 07:00 更新版\">Wikipedia</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:37:29');
INSERT INTO `game_softs` (`id`, `name`, `phonetic`, `phonetic2`, `phonetic_type`, `phonetic_order`, `genre`, `series_id`, `order_in_series`, `original_package_id`, `introduction`, `introduction_from`, `created_at`, `updated_at`) VALUES
(136, 'RESISTANCE～報復の刻～', 'れじすたんすほうふくのとき', 'れじすたんす21', 9, 307, '', 21, 3, 57, '物語の舞台は，RESISTANCE　～人類没落の日～から数週間後のヨーロッパ。\r\n謎のウイルス生命体“キメラ”の襲撃によって，ルクセンブルク，ボン，ロッテルダム，パリといったヨーロッパの各都市は，滅亡寸前という最悪の状況に陥っていた。\r\nしかし，人類もこの状況を黙って見ていたわけではない。\r\n人類の逆襲ともいうべき“欧州奪還作戦”が実行に移されるのだ。', '<a href=\"http://www.4gamer.net/games/075/G007508/20090219051/\" target=\"_blank\">4gamer</a>', '2018-03-24 10:17:50', '2018-05-29 22:37:29'),
(137, 'RESISTANCE2', 'れじすたんす2', 'れじすたんす20', 9, 306, '', 21, 2, 56, 'イギリス上陸作戦に参加した米国陸軍ネイサン・ヘイル軍曹の活躍により、人類は滅亡の危機を回避した。\r\nそれから2年後。1953年、キメラは突如アメリカ合衆国への侵入を開始する。\r\n空を覆う戦艦群、大地を踏み砕く歩行戦車、そしてビルをも薙ぎ倒す謎の巨大キメラ……その圧倒的な戦力を前に、人類は絶望する。\r\nそんな中、ネイサン・ヘイルと戦友たち、キメラウイルスによる遺伝子操作で強化された兵士たちによるセンチネルチーム・エコーは、アメリカ本土で決死の戦いを続けていた。\r\n人類を救うために……。', '<a href=\"https://ja.wikipedia.org/wiki/RESISTANCE2\" target=\"_blank\"><cite title=\"Wikipedia 2018年3月17日 (土) 06:57 更新版\">Wikipedia</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:37:29'),
(138, 'Necro-Nesia', 'ねくろねしあ', 'ねくろねしあ', 5, 204, '', NULL, NULL, 229, '昆虫生態の研究の為、レイとミシェルとマイクの三人はベルゼバーブ島に上陸する。\r\nしかし、その島は巨大昆虫の楽園だった。\r\n途中でミシェルとマイクとはぐれることになったレイは勇気を奮い起こし島の探索を始めることにする。\r\n\r\n果たして島に眠る秘密とは…？', '<a href=\"https://ja.wikipedia.org/wiki/%E3%83%8D%E3%82%AF%E3%83%AD%E3%83%8D%E3%82%B7%E3%82%A2\" target=\"_blank\"><cite title=\"Wikipedia 2017年9月8日 (金) 16:14更新版\">Wikipedia</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:49'),
(141, 'THE 大量地獄', 'ざたいりょうじごく', 'ざたいりょうじごく', 3, 112, '', NULL, NULL, 191, '生理的に「怖い」「気持ち悪い」生物が画面を大量に覆い尽くし無力な少女を追い回す…\r\nマジ恐怖のシチュエーション満載の地獄ゲーム。\r\n登場する敵はすべて気持ち悪く、恐い。\r\nとにかく逃げろ、生理的に嫌だ！', '<a href=\"http://www.comshop.co.jp/g-haku/index.php?jan=4527823994263\" target=\"_blank\"><cite title=\"ゲーム博物館\">パッケージ</cite></a> ※誤字修正', '2018-03-24 10:17:50', '2018-05-29 22:36:48'),
(142, 'darkSector', 'だーくせくたー', 'だーくせくたー', 4, 156, '', NULL, NULL, 216, '謎のウイルス、「テクノサイト」に冒された東ヨーロッパの街ラスニアが舞台。\r\n秘密情報組織は奇病が蔓延したラスニアへ、主人公（ヘイデン・テンノ）を調査に向かわせた。\r\nヘイデン自身もテクノサイト・ウイルスに感染してしまうが、感染により特殊能力を身に付け、その特殊能力を駆使し、同じくウイルス感染により怪物と化した市民やラスニアを支配する兵士、現地を巣食う悪霊達と戦いながら様々なミッションに挑む。', '<a href=\"https://ja.wikipedia.org/wiki/%E3%83%80%E3%83%BC%E3%82%AF%E3%82%BB%E3%82%AF%E3%82%BF%E3%83%BC\" target=\"_blank\"><cite title=\"Wikipedia 2017年9月21日 (木) 13:57更新版\">Wikipedia</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:49'),
(143, 'Red Seeds Profile -レッドシーズプロファイル-', 'れっどしーずぷろあいる', 'れっどしーずぷろあいる', 9, 308, '', NULL, NULL, 8, 'アメリカ北部の田舎町「グリーンベイル」にて若い女性の猟奇殺人事件が発生する。\r\n雨の夜に発生したその事件は、街に伝わる民間伝承（フォークロア）「レインコートキラー」の仕業ではないかと噂されていた。\r\n事件発生から数日後。やってきたFBI捜査官のフランシス・ヨーク・モーガンはこの事件と都市部で起こっていた謎の連続猟奇殺人が「赤い種（レッドシーズ）」で関連していると踏み、捜査を開始する。\r\n誰もが秘密を抱えるこの街に潜む闇とは、「レッドシーズ」の意味とは、そしてレインコートキラーの正体とは…。', '<a href=\"https://ja.wikipedia.org/wiki/%E3%83%AC%E3%83%83%E3%83%89%E3%82%B7%E3%83%BC%E3%82%BA%E3%83%97%E3%83%AD%E3%83%95%E3%82%A1%E3%82%A4%E3%83%AB\" target=\"_blank\"><cite title=\"Wikipedia 2018年4月8日 (日) 11:28 更新版\">Wikipedia</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:37:29'),
(144, 'THE DARKNESS', 'ざだーくねす', 'ざだーくねす', 3, 111, '', NULL, NULL, 189, '「おまえが闇を解き放つのだ」\r\nマフィアにヒットマンとして育てられたジャッキー・エスタカードは、21歳の誕生日を迎えた時に思いもよらぬプレゼントを授かる。\r\n先祖代々受け継がれるダークネスの力は絶大で、彼の存在を消そうとするマフィアを一掃する。\r\nしかし、その圧倒的パワーにも代償がつく。\r\nそれは街の平和か！愛する者の命か！自らの命か！\r\n闇はやがては闇に還るもの…', '<a href=\"http://www.comshop.co.jp/g-haku/index.php?jan=4940261509293\" target=\"_blank\"><cite title=\"ゲーム博物館\">パッケージ</cite></a> ※短縮のため一部削減', '2018-03-24 10:17:50', '2018-05-29 22:36:48'),
(145, 'レフト4デッド', 'れふとふぉーでっど', 'れふとふぉーでっど10', 9, 309, '', 23, 1, 41, 'Left 4 Dead は、Harf-Life® や Counter-Strike®などのクリエイターとして名高い Valve が放つ最新作です。\r\n本作では、襲い来るゾンビの群れやおぞましい突然変異ゾンビ「特殊感染者」に立ち向かう 4人の「生存者」の戦いが、まるで映画のようにゲーム化されています。', '<a href=\"http://l4d.zoo.co.jp/\" target=\"_blank\">公式サイト</a>', '2018-03-24 10:17:50', '2018-05-29 22:37:28'),
(146, 'レフト4デッド2', 'れふとふぉーでっど2', 'れふとふぉーでっど20', 9, 310, '', 23, 2, 45, 'Valveが放つ最新作として発売され「Best of E3」も受賞した、あの「LEFT 4 DEAD」の続編、「LEFT 4 DEAD 2」が堂々登場！\r\n\r\n襲い来るゾンビの群れやおぞましい突然変異ゾンビ「特殊感染者」に立ち向かう4人の「生存者」の戦いが、まるで映画のようにゲーム化！\r\nチームの団結なしには生存不可能なゾンビの黙示録の世界が、さらなるパワーアップを得て帰って来た！', '<a href=\"http://l4d.zoo.co.jp/\" target=\"_blank\">公式サイト</a>', '2018-03-24 10:17:50', '2018-05-29 22:37:28'),
(151, '人魚の烙印', 'にんぎょのらくいん', 'にんぎょのらくいん', 5, 203, '', NULL, NULL, 250, '飛行機が不時着した孤島は、邪教によって支配されていた。\r\n生贄に選ばれ、鱗に覆われた醜い姿に変貌していく恋人を救うことはできるのか…\r\n\r\n森魚、樹木等と融合した島民、ゴキブリの身体を持つ少女をはじめ、敵は奇想天外。\r\nさらには、突然噴出す間欠泉、瞬時に栄枯を繰り返す樹海等、自然さえもが襲いかかる。', 'パッケージ', '2018-03-24 10:17:50', '2018-05-29 22:36:49'),
(152, 'パラサイト・イヴ', 'ぱらさいといぶ', 'ぱらさいといぶ10', 6, 233, '', 25, 1, 233, 'それはクリスマス・イヴの夜、始まった…。\r\nニューヨーク市警の新人刑事アヤ・ブレアと、そのボーイフレンドはマンハッタンにあるオペラ座で、イヴの夜のデートを楽しんでいた。\r\nその突如、舞台上にいた役者が次々と発火していき…。', '<a href=\"http://www.jp.square-enix.com/archive/parasite_eve/3\" target=\"_blank\"><cite title=\"公式サイト\">公式サイト</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:48'),
(153, 'パラサイト・イヴ2', 'ぱらさいといぶ2', 'ぱらさいといぶ20', 6, 234, '', 25, 2, 393, 'マンハッタン島封鎖事件から3年――。\r\n事件後も全米各地では散発的にN.M.C.発生事件が相次ぎ、しかし確実に減少、縮小傾向へと向かっていた。\r\n事件後、アヤはカウンターN.M.C.専門機関「M.I.S.T.」に所属するN.M.C.ハンターとしてスカウトされ、警官を辞職。残存するN.M.C.の追跡を続けていた。\r\n平穏な日常を取り戻しつつあった彼女は、しかしある事件をきっかけに、再びあの惨劇の記憶に苛まれることになる。\r\n深く静かに息づく巨大な危機は、誰も知らぬ地の底から、けれど確実に、この愚かな世界を蝕もうとしていた……。', '<a href=\"https://ja.wikipedia.org/wiki/%E3%83%91%E3%83%A9%E3%82%B5%E3%82%A4%E3%83%88%E3%83%BB%E3%82%A4%E3%83%B42\" target=\"_blank\"><cite title=\"Wikipedia 2018年2月12日 (月) 03:52更新版\">Wikipedia</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:48'),
(154, 'The 3rd Birthday', 'ざさーどばーすでー', 'ざさーどばーすでー', 3, 109, '', 25, 3, 11, '喪失の過去から目覚めた一人の女性、アヤ・ブレア。\r\n遥か未来より人類を食いつくし、時間を逆走して現代へと到達した未知なる生物“ツイステッド”を倒しうる唯一の方法、「オーバーダイブ」を使って捜査に乗り出す。\r\n自身の過去、そして真実へと向かって──。', '<a href=\"http://www.jp.square-enix.com/archive/3rd_birthday/\" target=\"_blank\"><cite title=\"公式サイト\">公式サイト</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:48'),
(157, 'あかずの間', 'あかずのま', 'あかずのま', 1, 7, '', NULL, NULL, 27, '仕組まれた偶然に潜むのは、恍惚か？恐怖か？\r\n\r\nドラマの進行を決めるのはプレーヤー自身。\r\n数々の選択肢から、予期せぬドラマが展開するマルチエンディングストーリー。\r\n君はすべての教学のラストを体感することができるか!?', 'パッケージ', '2018-03-24 10:17:50', '2018-05-29 22:36:47'),
(158, '稲川淳二 恐怖の屋敷', 'いながわじゅんじきょうふのやしき', 'いながわじゅんじ10', 1, 22, '', 62, 1, 70, 'ようこそ恐怖の屋敷へ、私がここのオーナーの稲川淳二です。\r\n今からあなたをここの屋敷の様々な部屋にご案内します。\r\n心づもりはいいですか？\r\nもしいやだったらこのままおかえりになってもいいんですよ・・・・\r\nそれではご案内しましょう。どうぞ・・・・', 'パッケージ', '2018-03-24 10:17:50', '2018-05-29 22:37:28'),
(159, '逢魔が時', 'おうまがとき', 'おうまがとき', 1, 45, '', 27, 1, 86, '「闇」が支配するいにしえの世界「江戸」で、主人公が直面する様々な恐怖。\r\nそれらの正体を現代の知恵を駆使し、次々と解き明かしていく。\r\n2001年、夏、東京。都会の路地裏を駆け抜ける僕をまばゆい光が包んだ。\r\n気が付くと僕は、150年前の東京、「江戸」に横たわっていた。全ての記憶を失くして……。', '<a href=\"http://www.jp.playstation.com/software/title/slps91521.html\" target=\"_blank\"><cite title=\"プレイステーションオフィシャルサイト\">PSソフトウェアカタログ</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:48'),
(160, '逢魔が時2', 'おうまがとき2', 'おうまがとき2', 1, 46, '', 27, 2, 88, '「闇」が支配するいにしえの世界「江戸」で、主人公が直面する様々な恐怖。\r\nそれらの正体を現代の知恵を駆使し、次々と解き明かしていく。\r\n記憶を取り戻した主人公は、自らの意思で江戸に巣くう恐怖の源を解明していこうと決意する。', '<a href=\"http://www.jp.playstation.com/software/title/slps91522.html\" target=\"_blank\"><cite title=\"プレイステーションオフィシャルサイト\">PSソフトウェアカタログ</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:48'),
(161, '弟切草', 'おとぎりそう', 'おとぎりそう10', 1, 48, '', 28, 1, 90, 'ある夏の夕暮れ、山道で車を走らせていた主人公と同級生の奈美は、事故を起こしてしまいます。\r\n雷雨に見舞われ助けを求める2人の前に現れたのは古びた洋館。\r\nそこに足を踏み入れた2人が見たものは……。 ', '<a href=\"https://www.nintendo.co.jp/titles/20010000005427\" target=\"_blank\"><cite title=\"任天堂\">Wii U版公式サイト</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:48'),
(162, '弟切草-蘇生篇-', 'おとぎりそうそせいへん', 'おとぎりそう20', 1, 49, '', 28, 2, 91, '弟切草・・・。\r\nその花言葉は・・・復讐。\r\n\r\nある地方で今でも咲き実在する植物。\r\n弟を切り殺した返り血がその葉についたという不気味な言い伝えが残っている。', '<a href=\"http://www.jp.playstation.com/software/title/jp0365npjj00602_00000000000000000.html\" target=\"_blank\"><cite title=\"プレイステーションオフィシャルサイト\">PSソフトウェアカタログ</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:48'),
(163, 'かまいたちの夜', 'かまいたちのよる', 'かまいたちのよる10', 2, 56, '', 29, 1, 97, '舞台は山あいの地に建つペンションで、あなたは恋人と二人でここを訪れるのです。\r\nそして、そこで起こる恐るべき事件。\r\nあなたは、事件を解決し、無事にペンションを後にできるでしょうか…。', 'SFC版パッケージ', '2018-03-24 10:17:50', '2018-05-29 22:36:48'),
(164, 'かまいたちの夜2 監獄島のわらべ唄', 'かまいたちのよる2', 'かまいたちのよる20', 2, 57, '', 29, 2, 101, '謎の招待主に集められた十三人。\r\n囚人の怨念こもる監獄島にて不気味なわらべ唄が死を招く。', '<a href=\"http://www.comshop.co.jp/g-haku/index.php?jan=4932345028219\" target=\"_blank\"><cite title=\"ゲーム博物館\">パッケージ</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:48'),
(165, 'かまいたちの夜×3 三日月島事件の真相', 'かまいたちのよるとりぷるみかづきじまじけんのしんそう', 'かまいたちのよる30', 2, 58, '', 29, 3, 106, 'あの忌まわしき夜は、明けたはずだった。\r\n一年前、惨劇の部隊となった三日月島。\r\n呪いと密室の恐怖に満ちた三日月島。\r\n今、逃れられない恐怖に幕を引くため、彼らは秘められた真相の扉を開く', '<a href=\"http://www.comshop.co.jp/g-haku/index.php?jan=4974365831677\" target=\"_blank\"><cite title=\"ゲーム博物館\">パッケージ</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:47'),
(166, '黒ノ十三', 'くろのじゅうさん', 'くろのじゅうさん', 2, 79, '', NULL, NULL, 122, '人気ミステリー作家・綾辻行人氏監修による、13編のホラーノベルをオムニバス形式に構成。\r\n実力派揃いの執筆陣による作品が、プレイヤーを不思議な恐怖感の世界に誘う。\r\n実写取りこみをベースにした背景は1000画面を超え、シナリオごとに特長あるイメージを最新のCGのエフェクトで演出。', '<a href=\"http://www.jp.playstation.com/software/title/slps02811.html\" target=\"_blank\"><cite title=\"プレイステーションオフィシャルサイト\">PSソフトウェアカタログ</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:48'),
(167, '最終電車', 'さいしゅうでんしゃ', 'さいしゅうでんしゃ', 3, 97, '', NULL, NULL, 270, '遅くまでの残業のため、仕事の帰りにいつも乗っている最終電車。\r\nしかし、その日は何か様子が違っていた。\r\n最終電車は各駅停車のはずなのだが、自分の降りる駅で電車が止まらなかった。\r\n何故電車は止まらないのか、主人公はその謎を解こうと最後尾から先頭車両へと移動する。\r\nそこで主人公が見たものは・・・。', '<a href=\"https://ja.wikipedia.org/wiki/%E6%9C%80%E7%B5%82%E9%9B%BB%E8%BB%8A_(%E3%82%B2%E3%83%BC%E3%83%A0)\" target=\"_blank\"><cite title=\"Wikipedia 2016年8月18日 (木) 07:57更新版\">Wikipedia</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:48'),
(168, '大幽霊屋敷～浜村淳の実話怪談～', 'だいゆうれいやしきはまむらじゅんのじつわかいだん', 'だいゆうれいやしきはまむらじゅんのじつわかいだん', 4, 160, '', NULL, NULL, 219, '聞く恐怖　見る恐怖　読む恐怖\r\n\r\nストーリーテラー浜村 淳がこの夏、日本を氷づけにする。\r\nプレイステーション初の音声怪談ゲーム！', 'パッケージ', '2018-03-24 10:17:50', '2018-05-29 22:36:49'),
(169, '死者の呼ぶ館', 'ししゃのよぶやかた', 'ししゃのよぶやかた', 3, 122, '', NULL, NULL, 200, '恐怖と悪夢が織り成す迷宮で生き残るのは誰だ!?\r\n昼なお暗き森に迷い込んだ7人の男女と、彼らを迎える1人の老人…。\r\n謎を秘めた洋館が舞台の本格ホラーアドベンチャーゲーム！', 'パッケージ', '2018-03-24 10:17:50', '2018-05-29 22:36:49'),
(170, '吸血姫夕維～千夜抄～', 'ばんぱいあゆいせんやしょう', 'ばんぱいあゆいせんやしょう', 6, 237, '', NULL, NULL, 241, '厳格なミッションスクール\r\n春休み、誰もいない校舎に呼び集められた女生徒。\r\n遊び半分で始めた儀式の後、一人の生徒が消えた。\r\n闇に包まれた校舎の中、消えた友達を探してさまよう生徒たち。\r\n静かに忍び寄る恐怖の影は、現実か幻影か？', '<a href=\"http://www.comshop.co.jp/g-haku/index.php?jan=4543943000059\" target=\"_blank\"><cite title=\"ゲーム博物館\">パッケージ</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:48'),
(171, '彼岸花', 'ひがんばな', 'ひがんばな', 6, 242, '', NULL, NULL, 249, '咲き乱れる彼岸花、首ひとつ、指一本が飛んだ…\r\n    \r\n偶然出会ったはずの女子大生3人はそれぞれの思いを胸に京都を目指す。\r\n行く先々で巻き込まれる数々の事件は、「彼岸花」の怨念化、それとも・・・。', '<a href=\"http://www.comshop.co.jp/g-haku/index.php?jan=4991694000598\" target=\"_blank\"><cite title=\"ゲーム博物館\">パッケージ</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:50'),
(172, '閉鎖病院', 'へいさびょういん', 'へいさびょういん', 6, 262, '', NULL, NULL, 152, '時は大正末期。\r\n日本最大級の病院「国立大東亜病院」で、関係者が次々と高熱に倒れるという事件が発生した。\r\nなんとか回復したその中の1人・細川は、やがて院全体を巻き込む大事件に飲み込まれて行く…。', '<a href=\"https://ja.wikipedia.org/wiki/%E9%96%89%E9%8E%96%E7%97%85%E9%99%A2\" target=\"_blank\"><cite title=\"Wikipedia 2017年8月14日 (月) 06:10更新版\">Wikipedia</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:50'),
(173, '魔女たちの眠り', 'まじょたちのねむり', 'まじょたちのねむり10', 7, 267, '', 30, 1, 147, '主人公はどこにでもいる平凡なサラリーマン。\r\nある日、会社のデスクに電話がかかってきた。\r\nその内容は女性の声で、「助けて…殺される。」というものであった。\r\n翌日彼は奇妙な夢で目が覚めるが、その後で新聞の記事により電話の声の主は子供の頃の幼馴染だった女性とわかる。\r\n主人公は彼女が教師をしていたという分校がある山奥の村へと車を走らせるが…。', '<a href=\"https://ja.wikipedia.org/wiki/%E9%AD%94%E5%A5%B3%E3%81%9F%E3%81%A1%E3%81%AE%E7%9C%A0%E3%82%8A\" target=\"_blank\"><cite title=\"Wikipedia 2017年12月5日 (火) 18:00更新版\">Wikipedia</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:50'),
(174, '魔女たちの眠り-復活祭-', 'まじょたちのねむりふっかつさい', 'まじょたちのねむり20', 7, 268, '', 30, 2, 150, '平凡な日常をおくる僕に、突然かかってきた一本の電話。\r\nそれは僕を異世界へと誘う、学生時代の恋人の叫び声だった。\r\n閉鎖された町で起こる奇妙な連続殺人事件。\r\n巨大な密室と化した町で、君は真実にたどり着けるか。', '<a href=\"http://www.jp.playstation.com/software/title/jp0507npjj00503_000000000000000001.html\" target=\"_blank\"><cite title=\"プレイステーションオフィシャルサイト\">PSソフトウェアカタログ</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:50'),
(175, '夜光虫', 'やこうちゅう', 'やこうちゅう10', 8, 277, '', 42, 1, 138, '主人公は大型貨物船「ダイアナ」の船長。\r\n婚約者・友香に見送られて出発した「ダイアナ」の航海中、奇妙な出来事に遭遇する…。\r\n乗組員、また主人公は無事日本に帰還できるのか？', '<a href=\"http://s-novel.gc-mc.net/\" target=\"_blank\">サウンドノベルおぼえがき</a>', '2018-03-24 10:17:50', '2018-05-29 22:36:50'),
(176, '夜想曲', 'やそうきょく', 'やそうきょく10', 8, 279, '', 31, 1, 133, '大学卒業に必要な単位を取得するために、教授からアルバイトを紹介される。\r\nそれは、人里離れた図書館を擁する洋館で、住み込みで書庫の整理をする仕事だった。\r\nしかし、そこに集められたすべての本は、過去に死に関わってきたものばかりであった。\r\nそして、1冊の本をめぐりある奇怪な事件があなたを襲う…。', '<a href=\"http://www.jp.playstation.com/software/title/jp0507npjj00447_000000000000000001.html\" target=\"_blank\"><cite title=\"プレイステーションオフィシャルサイト\">PSソフトウェアカタログ</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:50'),
(177, '夜想曲2', 'やそうきょく2', 'やそうきょく20', 8, 280, '', 31, 2, 136, '人里離れた山奥にひっそりと佇む洋館‐野々宮図書館‐、死にまつわる本が集う場所。\r\nこの不気味な洋館に住み込みで働くことになった主人公を待っていたのは、地下に散乱する膨大な数の蔵書。\r\nその一つ一つの本に秘められた謎とは…？', '<a href=\"http://www.jp.playstation.com/software/title/jp0507npjj00449_000000000000000001.html\" target=\"_blank\"><cite title=\"プレイステーションオフィシャルサイト\">PSソフトウェアカタログ</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:50'),
(178, '19時03分上野発夜光列車', 'じゅうくじさんぷんうえのはつやこうれっしゃ', 'じゅうくじさんぷんうえのはつやこうれっしゃ', 3, 129, '', NULL, NULL, 207, '19時03分上野発、札幌行き寝台列車「北斗星5号」。\r\n主人公は札幌に行くために飛び乗った。\r\n北斗星5号は仙台を過ぎ次の停車駅の函館に向かっている。\r\n主人公はなにげにトイレに行き列車内に人がいないのに気づく。\r\n何かが起こっている、事件か。\r\n残された乗客はいるのか。\r\n主人公は列車内を探し始めた。\r\nはたしてそこには何が待ち受けているのか…', 'パッケージ ※句読点を変えました', '2018-03-24 10:17:50', '2018-05-29 22:36:49'),
(179, '実話怪談「新耳袋」一ノ章', 'じつわかいだんしんみみぶくろいちのしょう', 'じつわかいだんしんみみぶくろいちのしょう', 3, 126, '', NULL, NULL, 202, '怪談ファンの圧倒的な支持を誇る、ジャパニーズホラーの源流「新耳袋」がPSP（R)に登場。\r\n原作者の一人である木原浩勝氏を監修に迎え「怪談体感ソフト」として全50話の\"怪異\"を体感できます。\r\n全編フルボイスのナレーションや、PSP（R)初となる3Dサウンドが盛り上げる、本物の恐怖。\r\n特典映像には、数々のホラー映画で人気の三輪ひとみさん、南まいさん主演の新作ドラマを収録しています。', '<a href=\"http://www.jp.playstation.com/software/title/uljm05028.html\" target=\"_blank\"><cite title=\"プレイステーションオフィシャルサイト\">PSソフトウェアカタログ</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:49'),
(180, 'THE 呪いのゲーム', 'ざのろいのげーむ', 'ざのろいのげーむ', 3, 114, '', NULL, NULL, 192, '1人のプログラマーが生み出したゲーム。\r\nそれは、プレイした人間を絶望へと導く、呪いのゲームだった…。\r\n主人公であるあなたは、友達の智佳から借りた「THE 呪いのゲーム」をプレイしてしまう。\r\nその後、智佳との連絡が途切れ、あなたの周りでは次々と怪奇な現象が起こっていく。\r\nこのゲームに秘められた\"呪い\"とは？\r\nはたして、解く術はあるのだろうか？\r\n邪念と怨念に満ちた、このゲーム。\r\nあなたはこの呪いから逃れられない…。', '<a href=\"http://www.comshop.co.jp/g-haku/index.php?jan=4527823993464\" target=\"_blank\"><cite title=\"ゲーム博物館\">パッケージ</cite></a> ※誤字修正', '2018-03-24 10:17:50', '2018-05-29 22:36:48'),
(181, '赤川次郎月の光 ～沈める鐘の殺人～', 'あかがわじどうつきのひかりしずめるかねのさつじん', 'あかがわじどうつきのひかりしずめるかねのさつじん', 1, 6, '', NULL, NULL, 23, '新任教師であるあなたは、「紅葉坂」という名を頼りに、森の奥の女子高に向かっていた———\r\n坂の下であなたを出迎える紅葉たちが、まっすぐに坂を上ってゆく。\r\n長い紅葉坂を登ると、ふいに視界に現れた巨大な門。\r\n鐘のない鐘楼と、大きな池を擁する、「鐘園学園（しょうえんがくえん）」。\r\nそこには、幾つもの謎の事件があなたを待っていた・・・。\r\n\r\n歴史に埋もれた不可解な事件、学園に忍び寄る黒い影。\r\nあなたは、いくつの謎に迫ることができるのか！？', '<a href=\"http://www.marv.jp/special/game/ds/tsukinohikari/\" target=\"_blank\"><cite title=\"3DS版公式サイト\">3DS版公式サイト</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:47'),
(182, '歪みの国のアリス', 'ゆがみのくにのありす', 'ゆがみのくにのありす', 8, 287, '', NULL, NULL, 127, '『お帰り、僕らのアリス』\r\n\r\n主人公・葛木　亜莉子（かつらぎ　ありこ）は夕暮れの学校で目を覚まします。\r\nその目の前には、なぜかフードを被った怪しい男が。\r\n驚く亜莉子に、男はチェシャ猫と名乗り、こう言うのでした。\r\n\r\n　『さあ、僕らのアリス。シロウサギを追いかけよう』\r\n\r\n人の消えた世界、妙なケモノ、妙なヒトたち。\r\nアリス（=亜莉子）はチェシャ猫に導かれ、シロウサギを追いかけます。\r\nその果てに待つものとは……？\r\n\r\n\r\nそしてひとつの悲劇は、新たな惨劇を呼び起こす……。', '<a href=\"http://www.sun-denshi.co.jp/gsec/nmp/game/alice/index.php\" target=\"_blank\">公式サイト</a>', '2018-03-24 10:17:50', '2018-05-29 22:36:50'),
(183, '忌火起草', 'いまびきそう', 'いまびきそう10', 1, 26, '', 32, 1, 72, '目を合わせてはいけない。\r\nそのドラッグを口にした者は、”何か”が見え、そして焼け死ぬ……。', '<a href=\"http://www.comshop.co.jp/g-haku/index.php?jan=4974365835088\" target=\"_blank\"><cite title=\"ゲーム博物館\">パッケージ</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:48'),
(184, '忌火起草 解明編', 'いまびきそうかいめいへん', 'いまびきそう20', 1, 27, '', 32, 2, 73, '若者たちの間に流行するドラッグ「ビジョン」。\r\n遊び感覚でそれを手に入れたものは、次々と謎の焼死を遂げてしまう。\r\nその事件につながる「忌火起草」の悲しい運命、忌まわしき呪いとは…？', '<a href=\"http://www.comshop.co.jp/g-haku/index.php?jan=4974365142209\" target=\"_blank\"><cite title=\"ゲーム博物館\">パッケージ</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:48'),
(185, 'Dead Space', 'でっどすぺーす', 'でっどすぺーす10', 4, 178, '', 33, 1, 251, 'プレイヤーが演じる主人公は、宇宙船「USG Ishimura」に乗るエンジニアのアイザック・クラーク。\r\n戦士でもなく、兵士でもない。\r\nだが、残りの生存乗組員と恐ろしい不死の屍との攻防戦で、最後の砦となる。\r\n不死の屍アンデッドは、ゾンビとエイリアンの残虐なハイブリッド「ネクロモーフ」に変種する。\r\n従来の手段では倒せない怪物と化すのだ。\r\n幸運にもプレイヤーは、同等の斬新な攻戦力を備えており、脅威への対戦手段として強力な採掘ツールを自由に使える。', '<a href=\"https://www.ea.com/ja-jp/games/dead-space/dead-space\" target=\"_blank\">公式サイト</a>', '2018-03-24 10:17:50', '2018-05-29 22:36:49'),
(186, 'デッドスペース エクストラクション', 'でっどすぺーすえくすとらくしょん', 'でっどすぺーす11', 4, 179, '', 33, 2, 254, '遙か未来、地球の資源が既に枯渇し、人類が活路を求め宇宙開拓へと乗り出したスペースコロニー「イージス7（Aegis7）」が物語の舞台。\r\nイージス7で採掘作業を行っていた鉱山採掘船「USG イシムラ」だが、採掘した未知の鉱石から発せられた衝撃波に襲われる。\r\n破壊された採掘施設の修復に向かった作業員が正体不明の奇病に冒されたクリーチャーに襲われる。', '<a href=\"https://www.ea.com/ja-jp/games/dead-space/dead-space-extraction\" target=\"_blank\">公式サイト</a>', '2018-03-24 10:17:50', '2018-05-29 22:36:49'),
(187, 'Alan Wake', 'あらんうぇいく', 'あらんうぇいく', 1, 13, 'アクション サイコスリラー', NULL, NULL, 10, 'ベストセラー作家アラン ウェイクは、スランプを脱するために訪れた小さな田舎町で、最愛の妻アリスの失踪に見舞われる。\r\n彼女の行方を追うアランは、いつしか悪夢の世界へと足を踏み入れていた――。', 'XB360版パッケージ', '2018-03-24 10:17:50', '2018-05-29 22:36:47'),
(189, '夜光虫II 殺人経路', 'やこうちゅう2さつじんけいろ', 'やこうちゅう20', 8, 278, '', 42, 2, 140, '主人公である「不破 隆」は幼馴染の少女「早見 ありさ」に誘われ、豪華客船『ダイナスティ』の処女航海に参加する。\r\nしかし船上パーティの最中に船内で爆発事故が起こりダイナスティは沈没してしまう。\r\n主人公達は救命ボートにより難を逃れるが、他のボートとはぐれ漂流してしまった。\r\nそんなとき一行の目の前に突如霧の中から『パンドーラ』という名の客船が現れる。\r\nパンドーラはひと気がなく不気味な雰囲気を漂わせていたが、やむを得ず一行はその船に避難するのだった…。', '<a href=\"https://ja.wikipedia.org/wiki/%E5%A4%9C%E5%85%89%E8%99%ABII%E3%80%9C%E6%AE%BA%E4%BA%BA%E8%88%AA%E8%B7%AF%E3%80%9C\" target=\"_blank\"><cite title=\"Wikipedia 2018年3月8日 (木) 10:48更新版\">Wikipedia</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:50'),
(190, '厄痛 ～呪いのゲーム～', 'やくつーのろいのげーむ', 'やく20', 8, 276, '', 36, 2, 142, '幼なじみの高校生、みすずと省吾がゲーム会社でアルバイトを始めた…。\r\n会社社長も自信を持つ作品が出来上がったが、ゲーム批評をしている雑誌社からは、あまりにも冷たい評価が返ってきた…。\r\nこれと時を重ねるようにして、ゲーム会社の社長が失踪。その後、謎の死を遂げてしまう。\r\nプレイステーションソフト『厄 友情談疑』の型破りなアドベンチャーとしての部分はそのままに、新たな恐怖の世界が次々生み出されていく。', '<a href=\"http://www.jp.playstation.com/software/title/jp0031npjj00035_000000000000000001.html\" target=\"_blank\"><cite title=\"プレイステーションオフィシャルサイト\">PSソフトウェアカタログ</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:50'),
(191, 'リング∞', 'りんぐいんふぃにてぃー', 'りんぐいんふぃにてぃー', 9, 300, '', NULL, NULL, 196, 'あなたに残された期限は一週間\r\n謎の呪いに誘われるあなたの運命は…\r\n選択肢によって、導かれる数々の恐怖、そして結末。\r\n例え生き残ったとしても、そこにはまだた、新たな呪いが…', 'パッケージ', '2018-03-24 10:17:50', '2018-05-29 22:37:29'),
(193, 'ゴーストヴァイブレーション', 'ごーすとばいぶれーしょん', 'ごーすとばいぶれーしょん', 2, 83, 'ホラーアクション', NULL, NULL, 199, 'ある日、ゴーストハンター・ジョージの元に彼の幼馴染・アリシアから依頼の電話がかかってきた。\r\n依頼の内容に不審感を抱いたジョージは、彼女に頼まれたゴースト退治の道具一式と共に現地へと赴く。', '<a href=\"https://ja.wikipedia.org/wiki/%E3%82%B4%E3%83%BC%E3%82%B9%E3%83%88%E3%83%B4%E3%82%A1%E3%82%A4%E3%83%96%E3%83%AC%E3%83%BC%E3%82%B7%E3%83%A7%E3%83%B3\" target=\"_blank\"><cite title=\"Wikipedia 2017年8月3日 (木) 12:22 更新版\">Wikipedia</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:48'),
(194, 'おおかみかくし', 'おおかみかくし', 'おおかみかくし', 1, 47, '', NULL, NULL, 235, '16際の少年 九澄 博士は、都心から離れた山間にあるニュータウン嫦娥町で暮らすこととなった。\r\n河川が”旧市街”と”新市街”を分断するこの町は、今もまだ謎に包まれた土地文化や風習が数多く残っている。\r\n\r\n取り立てて目立つ存在ではなかった博士をクラスメイトはあり得ないぐらい気にかけてくれる。\r\nそんな環境に戸惑いつつも新しい生活を楽しむ博士だが、ただ１人、クラス委員を務める櫛名田 眠だけが明らかに博士と距離を置いているのが気がかりだった。\r\n数少ない接触の中で、彼女は博士に忠告する。\r\n「旧市街には近づかないように」\r\n\r\nそこに何があるのか？・・・そして恐怖と謎が交錯する。', '<a href=\"http://www.konami.jp/products/okamikakushi_psp/\" target=\"_blank\"><cite title=\"公式サイト\">公式サイト</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:48'),
(195, '学校であった怖い話S', 'がっこうであったこわいはなしえす', 'がっこうであったこわいはなし20', 2, 54, '', 34, 2, 236, '2007年秋発売予定のPlayStation®2用ソフト『四八(仮)』のディレクターである飯島多紀哉氏とバンプレストがPlayStation®で初めてコラボレーションした記念すべき作品。\r\nジャンルはサウンドノベル形式のアドベンチャーゲーム。\r\n主人公は六人の語り手の中から一話ずつ怖い話を聞き、それを追体験していく。\r\nマルチシナリオ・マルチエンディング採用による、主人公の選択次第で変化する誰も予測ができない恐怖の結末！\r\nストーリーは話を聞く順序や選択、主人公の性別によって幾重にも変化し、特別な条件を満たしているときのみ現れる隠しシナリオも用意。\r\n無数の恐怖があなたを待っています。\r\nさあ、恐怖の物語をはじめましょう。 ', '<a href=\"https://store.playstation.com/ja-jp/product/JP0502-NPJJ00077_00-0000000000000001\" target=\"_blank\"><cite title=\"PlayStation Store\">PlayStation Store</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:48'),
(196, 'S.T.A.L.K.E.R.: Shadow of Chernobyl', 'すとーかーしゃどーおぶちぇるのぶいり', 'すとーかー10', 3, 139, '', 43, 1, 518, '1986年に爆発を起こしたチェルノブイリ原子力発電所。この跡地で2006年に再び謎の大爆発が起きる。\r\nこの爆発の影響により30k㎡に及ぶ広い範囲が核汚染の被害にあう。\r\n\r\n時は経ち2012年。\r\n\r\n汚染された跡地一帯は、突然変異によるモンスター達が生息する「ゾーン」と呼ばれる危険地帯と化した。そんなゾーン周辺には生活するためのビジネスが成り立ちはじめる。危険な依頼を請け負いその見返りに多額の報酬を受け取り生計をたてていく「ストーカー」と呼ばれる荒くれ者達がゾーン周辺で暮らし始めていた。\r\nプレイヤーはゾーン内に入ったばかりのストーカーとなって幾多のクエストをこなしながら、大爆発を起こした本当の謎を解き明かしていく！', '<a href=\"http://stalker.zoo.co.jp\" target=\"_blank\">公式サイト</a>', '2018-03-24 10:17:50', '2018-05-29 22:37:28'),
(197, 'S.T.A.L.K.E.R.: Clear Sky', 'すとーかーくりあすかい', 'すとーかー20', 3, 140, '', 43, 2, 519, '前作、「ストーカー ～シャドーオブチェルノブイリ～」の1年前、2011年の物語。\r\n謎のチェルノブイリ原発爆発により、危険地帯と化した「ゾーン」。不安定な大気の中、プレイヤーの周りで見え隠れする「ゾーン」の謎とは…。\r\nなぜ、Strelokはトラック事故死したのか?それ以前のStrelokに何があったのか？他に選択すべき方法はなかったのか？また、不安定なゾーンを元に戻す方法はあるのか？\r\n今、すべての謎が解き明かされる！', '<a href=\"http://stalker.zoo.co.jp\" target=\"_blank\">公式サイト</a>', '2018-03-24 10:17:50', '2018-05-29 22:37:28'),
(198, '腐り姫～euthanasia～', 'くさりひめ', 'くさりひめ', 2, 69, 'インモラルホラーアドベンチャー', NULL, NULL, 328, ' 父親と妹が怪死を遂げ、記憶喪失となった主人公は、義母に連れられ、故郷の町へと戻る。そこで主人公は蔵女（くらめ）と呼ばれる、深紅の着物の少女と出逢う。\r\n\r\n少女は自分の妹に瓜二つだった。\r\n\r\n取り巻く家族や友人たちは、うわべでは彼の回復を望みながらも、罪の意識を心に潜ませている。蔵女はそんな人々の隙に取り入り、甘美な肉欲と狂気を与え、身も心も崩壊させていく。\r\n\r\n記憶と現実の境界が揺らぎ、喪失感と、蘇る恐怖との狭間に葛藤しながら、やがて全ての記憶を取り戻し、赤い雪が降り積もるなか、世界が死の静寂に包まれるまでの４日間。', '<a href=\"http://www.liar.co.jp/kusaritop.html\" target=\"_blank\">公式サイト</a>', '2018-03-24 10:17:50', '2018-05-29 22:37:28'),
(199, '朝の来ない夜に抱かれて -ETERNAL NIGHT-', 'あさのこないよるにだかれてえたーなるないと', 'あさのこないよるにだかれてえたーなるないと', 1, 9, '学園伝記ホラーアドベンチャー', NULL, NULL, 329, '愛するものを守るため、邪神をその身に宿したあなたは、異世界の存在を招く「異界の門」より生じた化け物との対決を迫られます。\r\n日本古来の説話、伝説を題材にした5つの物語。\r\nそれは、あなたの運命を巻き込み、より大きな物語を紡いでいきます。\r\n結末は果たして…', '<a href=\"http://www.comshop.co.jp/g-haku/index.php?jan=4988715006349\" target=\"_blank\"><cite title=\"ゲーム博物館\">パッケージ</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:37:29'),
(200, '蛍子', 'ほたるこ', 'ほたるこ', 6, 263, '伝奇ホラーアドベンチャー', NULL, NULL, 333, '主人公は彼女が見たフラッシュバックの原因を探るため、彼女の故郷である島へ向かう。\r\n彼女をこの島の親族に預けている間に、島を探索することにした。\r\nそして、屋敷のそばにある神社で神主とその娘・菱原涼と知り合うのだった。\r\n\r\nある日、涼の両親が殺人事件に巻き込まれた。\r\n小さな島は、異様な雰囲気と変わり、主人公も命の危険を感じ始めていた…。', '<a href=\"http://www.getchu.com/soft.phtml?id=16743\" target=\"_blank\">Getchu.com</a>', '2018-03-24 10:17:50', '2018-05-29 22:36:50'),
(201, 'Esの方程式', 'えすのほうていしき', 'えすのほうていしき', 1, 41, 'パースペクティヴモダンホラーアドベンチャー', 44, 2, 338, '前作『黒の断章』から一ヵ月後、草薙のかつての同僚2人が同時刻に自殺する事件が発生した。\r\nその事件を追ううち、それが草薙にとって忘れることが出来ない三年前の事件とリンクしていることがわかった。\r\nそしてさらに、神と奉り上げられた自閉症の少年・鵺野、雲頭様（ワンズサマ）と云う奇妙な信仰を持つ小さな漁村で11年前に起こった虐殺事件へと結び付いて……。', '<a href=\"http://www.getchu.com/soft.phtml?id=660867\" target=\"_blank\">Getchu.com</a>', '2018-03-24 10:17:50', '2018-05-29 22:37:28'),
(202, '3days -満ちてゆく刻の彼方で-', 'すりーでいずみちゆくときのかなたで', 'すりーでいずみちゆくときのかなたで', 3, 142, '無限軌道オカルティックサスペンスAVG', NULL, NULL, 342, '平凡な学生として日常を送り続けていた主人公・高梨亮は、10月16日を始まりとした3日間、連続して非日常な現象に見舞われる。\r\n1日目は同じ綾篠学園に通う柊美柚の殺害。2日目は立て続けに起こった吾妻梨花の飛び降り自殺。\r\nそして3日目は藤見たまきとの性交中、自宅に現れた襲撃者に2人とも惨殺されるというものであった。\r\nところが、惨殺されたはずの亮は10月16日の朝に再び目覚める。\r\n亮は鮮明には思い出せない吐き気を催すほどの嫌悪感と、記憶の混乱による不快感に陥りつつ、先程までの恐怖の残滓を悪夢と割り切り、登校を始める。\r\nそして道中、柊美柚の殺害現場を目撃した亮は、奇怪な既視感を覚える。\r\nそれは、無限に繰り返される3日間の始まりであった。', '<a href=\"https://ja.wikipedia.org/wiki/3days_%E3%80%9C%E6%BA%80%E3%81%A1%E3%81%A6%E3%82%86%E3%81%8F%E5%88%BB%E3%81%AE%E5%BD%BC%E6%96%B9%E3%81%A7%E3%80%9C\" target=\"_blank\"><cite title=\"Wikipedia 2016年4月3日 (日) 01:20 更新版\">Wikipedia</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:37:28'),
(203, 'BIFRONTE ～公界島奇譚～', 'びふろんてくがいとうきたん', 'びふろんてくがいとうきたん', 6, 248, '', NULL, NULL, 343, '本州の某県某半島の南端にぽつんと浮かぶ島ーー『公界島』。\r\nその島では『救世ノ家』という宗教団体が多くの島民たちの心を掴み、信者の数を増やしていた。\r\n\r\n主人公・山田草一郎は、そんな宗教色の強い島に育ったが、大学進学を期に、島を離れる日が近づいていた。\r\n幼馴染で腐れ縁の天谷桜・若菜姉妹、貝塚仁たちが、最後の思い出作りと称して、肝試しをしたいと持ちかけてくる。\r\n\r\n行き先は『高波邸』――\r\nそこには五年前、当時教団の広告塔を務めていた子役アイドルが住んでいた。\r\nだが、とある日、子役アイドルとその家族は、謎の怪死を遂げてしまう。\r\nそれ以降、その邸宅は心霊スポットとして有名になっていった。\r\n\r\nそして惨劇は始まる…。', '<a href=\"http://dlsoft.dmm.co.jp/detail/bluegale_0022/\" target=\"_blank\">DMM</a> ※短縮のため一部削減', '2018-03-24 10:17:50', '2018-05-29 22:36:50'),
(204, 'DIVI-DEAD', 'でぃヴぁいでっど', 'でぃヴぁいでっど', 4, 172, '', NULL, NULL, 344, '主人公が”狩野山学院高等部”に転校した次の日から物語は始まる。\r\nこの全寮制の学院の学長は、主人公にとっては血の繋がりのあやふやな叔父にあたるが、今回の転校もこの叔父の指示である。\r\n叔父の「親切」が全く信用できない主人公は、 自分の転校理由は、叔父が外部に漏らしたくない不祥事を調査し報告もしくは解決することだと判断し行動する。\r\n実際、校内にはどうみても情緒不安定で凶暴な生徒たちがおり、 主人公は校内で密かに広まりつつある”香”とかれらの存在を結び付け調査を始める。\r\nところが転校と同時に彼にもまた妙なことが起こり始める。\r\n主人公のことを知っていると言って平然と夜這いまでかけてくるお嬢様、 幼なじみにも関わらずそれを否定する少女、 夜な夜な、酷い時には白昼にまで見る奇妙な夢・・・・・・。\r\n結局事態は殺人にまで発展する・・・・・', '<a href=\"http://www.getchu.com/soft.phtml?id=424\" target=\"_blank\">Getchu.com</a>', '2018-03-24 10:17:50', '2018-05-29 22:37:28'),
(205, 'DOOP', 'どぉーぷ', 'どぉーぷ', 4, 188, '深海探索アドベンチャー', NULL, NULL, 346, '', '', '2018-03-24 10:17:50', '2018-03-24 10:20:04'),
(206, 'EXTRAVAGANZA ～蟲愛でる少女～', 'えくすとらヴぁがんざむしめでるしょうじょ', 'えくすとらヴぁがんざむしめでるしょうじょ', 1, 37, '', NULL, NULL, 347, '薄汚れた床の上に、少女の子供は産み落とされた――。\r\n救いの手はなく、抗うだけの力も持たず、ただの道具として扱われる日々。\r\n求められた苗床という役目は、少女から家族や友人、普通の生活、そして処女といった様々なものを奪い、薄暗い部屋へと閉じ込めた。\r\n眼前に在るのは、おぞましき異形の生物。\r\n腐臭漂う粘ついた触手が、少女のきめ細やかな柔肌を這いずり回り、ありとあらゆる孔に潜り込んでいった。\r\n\r\n終わりはどこにあるのか。\r\n果てには何があるのか。\r\n\r\n膨らんだ腹の中で何かが蠢く苦痛に苛まれながら、やがて少女は汚物とともに蟲を産む。\r\n快楽と苦痛の終わりに、恐怖と狂気の果てに、自らの子供を胸に抱いて泣いた。\r\n\r\n「あたしと一緒に……来る……？」\r\n\r\n唐突に得られた自由。\r\n月の明るい夜に、少女は久しぶりに外に出た。\r\n血まみれのシーツを身にまとい、裸足で冷たい大地の上に立つ。\r\n行くべき場所などなかったが、少なくとも独りではない。\r\n傍らでは一匹の蟲が、まるで母親に甘えるように少女の足に擦り寄っていた。\r\n\r\nそして――以降十五年に渡る、少女の物語の幕が上がる――――。', '<a href=\"http://www.cyc-soft.com/b-cyc-pro/extra/extra_form.htm\" target=\"_blank\">公式サイト</a>', '2018-03-24 10:17:50', '2018-05-29 22:36:49'),
(207, 'Love Letter', 'らぶれたー', 'らぶれたー', 9, 296, '猟奇サスペンスアドベンチャー', NULL, NULL, 349, '主人公（卓哉）を中心に、血の惨劇が繰り返される。\r\n卓哉に近づく女性の皮膚が切り取られ……無残な惨殺シーンが、ビデオに撮られ送られる。\r\nだが、そんな猟奇的行為も……事件に潜む狂気と欲望の、一端でしかなかった……\r\nやがて事態は、卓哉の隠された過去へもシンクロし……意外な方向へ走りだす。\r\nはたして卓哉の未来には、どんな結末が待っているのか……', '<a href=\"http://www.comshop.co.jp/g-haku/index.php?jan=4528228000023\" target=\"_blank\"><cite title=\"ゲーム博物館\">パッケージ</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:47'),
(208, 'つくとり', 'つくとり', 'つくとり', 4, 167, '', NULL, NULL, 350, '古い因習と伝統に縛られた人口1000人あまりのひなびた町、月鳥町では、毎年祭りの季節が近づくと必ず「ツクトリ様のいけにえ」とされる木に吊るされた死体が発見されるという。\r\nひょんなことから事件の捜査を手伝うことになった主人公橋上郁は、奇怪な事件を通して複雑な事情を抱えるヒロインたちと出会っていく。\r\nツクトリ様は実在するのか？\r\nそして月鳥町に隠された真実とは？', '<a href=\"http://www.getchu.com/soft.phtml?id=374825\" target=\"_blank\">Getchu.com</a>', '2018-03-24 10:17:50', '2018-05-29 22:36:49'),
(209, 'ほとせなる呪 ちとせなる詛', 'ほとせなるうた　ちとせなるしるし', 'ほとせなるうた　ちとせなるしるし', 6, 264, '', NULL, NULL, 351, '主人公のもとに、遠くはなれた故郷から幼馴染の少女が訪ね来た。\r\n少女の目的は、同郷で主人公の遠縁の親戚にあたる男が死んだことを、男のたった一人の肉親に伝えること。\r\nだが、男の肉親も、男同様に奇怪な死を遂げた後だった…。\r\nその時を前後して、主人公の近辺に暗躍する謎の人物たちの姿があった。 続々と怪死事件は増え続け、歩調を併せるように急激な広がりを見せる都市伝説“閉ざされた少女からのメール”の存在も明らかになる。\r\nその事件の渦中に巻き込まれてゆくと共に、主人公の日常が、ゆっくりと、そして確実に、それまで非現実であったものに侵食されてゆく…！！\r\n\r\n呪われた都市伝説によって突きつけらーれる死の宣告、身近な人々に、次々と忍び寄る死の影。\r\n都会の闇の中に蠢く、神世の時代より息づく「呪詛」とは…？', '<a href=\"http://www.getchu.com/soft.phtml?id=16085\" target=\"_blank\">Getchu.com</a>', '2018-03-24 10:17:50', '2018-05-29 22:36:50'),
(210, 'アノニマス', 'あのにます', 'あのにます', 1, 10, '', NULL, NULL, 352, '主人公・鹿島浩介は、妹が引き籠もりになるという事態を受けて都会に引っ越しをすることになった。\r\n偶然にも、転校した学園には今年の夏に海水浴で知り合った、滝野夏葵がいた。\r\nしかし、夏葵はなぜか浩介に敵意をむき出しにしてくる。\r\n\r\nそんなある日、浩介の携帯にAnonymousを名乗る相手からメールが届く。\r\n「お前の秘密を知っている」\r\n詳しいことは分からないが、それは夏葵と知り合い、妹が引き籠もる原因となった「何か」があったあの夏の日に、「秘密」があることを示唆していた。\r\n\r\n浩介は悪い予感を感じながらも、その「秘密」にたどり着こうとする。\r\nだが、浩介は甘すぎたのだ。後悔は決して先には立たないのである……。', '<a href=\"http://www.getchu.com/soft.phtml?id=245725&gc=gc\" target=\"_blank\">Getchu.com</a> ※短縮のため一部削減', '2018-03-24 10:17:50', '2018-05-29 22:36:49'),
(211, 'カルタグラ～ツキ狂イノ病～', 'かるたぐらつきぐるいのやまい', 'かるたぐらつきぐるいのやまい', 2, 59, '', NULL, NULL, 354, 'かつて警視庁に籍を置いていた主人公は、世話になった上司の頼みで良家息女の失踪事件を調査する事となった。\r\n事件のあった上月家で、かつて逢瀬を交わした恋人と同じ顔を持つ少女と出会う。\r\nそして彼女から、消えた双子の姉を捜し出して欲しいと懇願される。\r\nだが彼女の父親は、こう告げた。\r\n「あの娘は、本当は死んでいるんですよ……」\r\n交錯する虚構と真実。戦後間もない上野の町を舞台にして、今、惨劇の幕が開く――。', '<a href=\"http://www.gungnir.co.jp/innocentgrey/products/pro_cartagra/index.html\" target=\"_blank\">公式サイト</a> ※短縮のため改変', '2018-03-24 10:17:50', '2018-05-29 22:37:28'),
(212, 'ゴア・スクリーミング・ショウ', 'ごあすくりーみんぐしょう', 'ごあすくりーみんぐしょう', 2, 88, '', NULL, NULL, 355, '両親の渡米を機に、俺……『仁野恭司』は生まれ故郷の田舎町『呉仁（くれひと）町』に帰ってきた。\r\nそこで俺は、それぞれ縁のあった3人の少女たちと再会する。\r\n時折、小さな波風が立つことはあっても、平穏で退屈な日々が続いていくはずだった日常。\r\n\r\nだが、過去の記憶に呼ばれ出会ったひとりの美少女と『ゴアスクリーミングショウ』と名乗る怪人物。\r\n彼等との出会いが、俺たちの平凡な日常に小さな亀裂をつくり、狂気と絶望に彩られた奇怪なる『非日常』へと変貌させていく―――。', '<a href=\"http://dlsoft.dmm.co.jp/detail/beye_0033/\" target=\"_blank\">DMM</a>', '2018-03-24 10:17:50', '2018-05-29 22:37:28'),
(213, '闇色の童話', 'やみいろのどうわ', 'やみいろのどうわ', 8, 281, '', NULL, NULL, 358, '夏休み、大学のサークル仲間で合宿旅行に出かけた主人公達。\r\n目的地は、サークル仲間の１人の親戚がはじめたというリゾートペンションだった。\r\n長いこと電車やバスに揺られ、やっと着いたと思えばまたアクシデント。\r\nバス路線の終点でいくら待っても来るはずの迎えがやってこない。仕方なくペンションに向かって歩き始めた主人公達だったが・・・。', '<a href=\"http://www.getchu.com/soft.phtml?id=156\" target=\"_blank\"><cite title=\"Getchu.com\">Getchu.com</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:47'),
(214, '果てしなく青い、この空の下で…。', 'はてしなくあおいこのそらのしたで', 'はてしなくあおいこのそらのしたで', 6, 228, '', NULL, NULL, 360, '生徒数わずか6人という安曇学園に通う戒田正士は、始業式の日に突然、1年後に母校が廃校になることを伝えられる。\r\n時の流れとともに変わることを余儀なくされた学園、安曇村、そして生徒たち……。\r\n皆は残された学園生活の中でそれぞれ想いを深めようとする。\r\nそんな中、正士は村の開発に便乗して金儲けをしようとする元代議士・堂島の悪巧みに巻き込まれる。\r\n正士の周りの状況は次第に変化し、その中には古より安曇村に伝わるヤマノカミの姿が見え隠れしていた。', '<a href=\"https://ja.wikipedia.org/wiki/%E6%9E%9C%E3%81%A6%E3%81%97%E3%81%AA%E3%81%8F%E9%9D%92%E3%81%84%E3%80%81%E3%81%93%E3%81%AE%E7%A9%BA%E3%81%AE%E4%B8%8B%E3%81%A7%E2%80%A6%E3%80%82\" target=\"_blank\"><cite title=\"Wikipedia 2017年2月28日 (火) 05:23更新版\">Wikipedia</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:50'),
(215, 'ダイアの約束 ～逢いたくて～', 'だいあのやくそくあいたくて', 'だいあのやくそくあいたくて', 4, 159, '', NULL, NULL, 361, '恐怖に布団へ潜り込んだ潤一郎を揺り起こしたのは、幼稚園が一緒だった幼馴染みの“清沢 桜”だった。\r\n突然やってきた桜に色々な疑問を抱きつつ、彼女と一緒に暮らす潤一郎だったが、ある時、彼女の隠された正体を知ってしまう…。\r\n桜に隠された真実に、潤一郎はどんな思いを抱き、彼女に接していくのか……。', '<a href=\"http://dlsoft.dmm.co.jp/detail/bluesky_0005/\" target=\"_blank\">DMM</a>', '2018-03-24 10:17:50', '2018-05-29 22:36:49'),
(216, '沙耶の唄', 'さやのうた', 'さやのうた', 3, 119, 'サスペンスホラーADV', NULL, NULL, 362, '爛れてゆく。何もかもが歪み、爛れてゆく。\r\n交通事故で生死の境をさまよった匂坂郁紀は、いつしか独り孤独に、悪夢に囚われたまま生きるようになっていた。 彼に親しい者たちが異変に気付き、救いの手を差し伸べようにも、そんな友人たちの声は決して郁紀に届かない。 \r\nそんな郁紀の前に、一人の謎の少女が現れたとき、彼の狂気は次第に世界を侵蝕しはじめる。', '<a href=\"http://www.nitroplus.co.jp/game/06-song-of-saya/saya.php\" target=\"_blank\">公式サイト</a>', '2018-03-24 10:17:50', '2018-05-29 22:37:28'),
(218, '雑音領域', 'ざつおんりょういき', 'ざつおんりょういき', 3, 113, 'サイコホラーアドベンチャー', NULL, NULL, 366, '本家の松本直人は14歳以前の記憶が無く、両親も知らない。\r\n池内家で育てられ、成人後は一人暮らしをしていた。\r\nしかし里親から呼び出され故郷からの帰り道、奇妙で不思議な、そして秘められた領域に迷い込む。\r\nそう琥珀の時間の中で･･････妖しく光る記憶と繚乱の領域へと･･････。', '<a href=\"http://www.do-game.co.jp/r18/products/%E9%9B%91%E9%9F%B3%E9%A0%98%E5%9F%9F/\" target=\"_blank\">公式サイト</a>', '2018-03-24 10:17:50', '2018-05-29 22:37:28'),
(219, '肢体を洗う', 'したいをあらう', 'したいをあらう', 3, 124, '', NULL, NULL, 368, '貧乏浪人生である八坂は、医者になりたいという一心から、ある大学付属病院の事務局でバイトをしながら浪人生活を送っていた。\r\nしかしそんな恵まれた時間も長くは続かなかった。\r\n突然、女副院長・露崎千草からバイトの解雇を言い渡されてしまったのだ。\r\n突然の出来事に打ちひしがれる主人公。\r\nそんな彼に彼女は病院に残るための唯一の手段を提示してくる。\r\n彼女が代わりに用意してくれた仕事とは……俗に「死体洗い」と言われる仕事だった。\r\nあなたは、作者が実体験を元に著述するこの作品をプレイすることによって狂愕の世界を知ることになるだろう――\r\n日常、垣間見ることができない「死体洗い」という仕事を通じ「日常から非日常に放り込まれた主人公の精神的な変遷」――\r\nそれが『肢体を洗う』だ。', '<a href=\"http://dlsoft.dmm.co.jp/elf/shitai/\" target=\"_blank\">DMM</a>', '2018-03-24 10:17:50', '2018-05-29 22:37:28'),
(221, '二重影', 'にじゅううかげ', 'にじゅううかげ', 5, 202, '', NULL, NULL, 372, '寛永17年。\r\nその風貌ゆえに人々から忌み嫌われる剣士、二つ影双厳。\r\n双厳は人が必ず双子で生まれ、そして何百年に一度の例外を除いて一方が間引かれるという呪われた風習を持つ、淡島にやってきた。\r\n決まってイル・スイと名づけられること、例外として双子のまま残される姉妹の謎、そして双厳自身の2つの影とは何なのか。', '<a href=\"https://ja.wikipedia.org/wiki/%E4%BA%8C%E9%87%8D%E5%BD%B1\" target=\"_blank\"><cite title=\"Wikipedia 2017年8月19日 (土) 06:47更新版\">Wikipedia</cite></a> ※短縮のため改変', '2018-03-24 10:17:50', '2018-05-29 22:36:50'),
(222, '蜜柑', 'みかん', 'みかん', 7, 269, '', NULL, NULL, 373, '古本屋「蜜柑屋」に入った主人公は、本棚を見ると自らの未完の小説「虚ろなる器」を見付ける。\r\nヒロインのイメージが浮かばない事から、筆を止めてしまっていた未発表の小説で、続きを書こうとすると、まるで自分が物語の世界に入り込んだかのように、想像の世界が目前に広がる。登場するヒロイン達は彼の描いた物語のままに行動するが、その物語の中に登場しないはずの第2のヒロインが現れる。\r\n彼女だけは彼の予期せぬ行動を取り、その存在はやがて物語に波乱を呼んでいく。\r\n何故居るはずの無い彼女が存在し、何故「虚ろなる器」は未完なのか・・・。', '<a href=\"http://www.amazon.co.jp/%E3%82%B7%E3%83%BC%E3%82%BA%E3%82%A6%E3%82%A7%E3%82%A2-%E8%9C%9C%E6%9F%91/dp/B000065D6B%3FSubscriptionId%3DAKIAINELD3CSNUTU2R4Q%26tag%3Dh-g-s-22%26linkCode%3Dxm2%26camp%3D2025%26creative%3D165953%26creativeASIN%3DB000065D6B\" target=\"_blank\">Amazon</a>', '2018-03-24 10:17:50', '2018-05-29 22:36:50'),
(223, '牝姫の虜 ～廃校舎の制服少女～', 'めひめのとりこはいこうしゃのせいふくしょうじょ', 'めひめのとりこはいこうしゃのせいふくしょうじょ', 7, 274, '', NULL, NULL, 374, '山間にひっそりたたずむ私立桜陵学園に、古鷹隆介は転校してきた。\r\n楽しい学園生活を送る隆介だったが、今は使われていない旧校舎が彼を呼ぶ。\r\n足を踏み入れた彼が見たのものは、明らかに正気を失っている様子で男を求める少女たち。\r\n淫らな宴に魅入られ、引きこまれてゆく隆介。\r\nその前に現れる謎の少女。\r\n旧校舎にまつわる秘密とは何か？\r\n封じこめられた想いとは？\r\n謎の少女は隆介に何をさせようというのだろう。\r\n\r\n今、淫魔宴が始まる……。', '<a href=\"http://www.getchu.com/soft.phtml?id=285305\" target=\"_blank\">Getchu.com</a>', '2018-03-24 10:17:50', '2018-05-29 22:36:50'),
(224, '幽明境を異にする', 'ゆうめいさかいをことにする', 'ゆうめいさかいをことにする', 8, 285, '', NULL, NULL, 375, '旧校舎で手に入れた古ぼけた箱………\r\nその箱は、カードを一枚ずつ出せる程度の隙間があるだけで空けることができない。\r\n内気な女の子が想いを告げるために用意したもののようだ。\r\n\r\n『恋遊びの文札』の指示に従いながら私を探し当ててください。\r\n\r\n興味本位で『恋遊びの文札』の指示に従ってみる二人。\r\nそれがゲームの開始だった。\r\n次々とカードの指示通りに行動していき、ついに『それ』を引いてしまう……', '<a href=\"http://www.comshop.co.jp/g-haku/index.php?jan=4528228000078\" target=\"_blank\"><cite title=\"ゲーム博物館\">パッケージ</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:47'),
(225, '螺旋回廊', 'らせんかいろう', 'らせんかいろう10', 9, 293, '', 45, 1, 377, '内容の異変に気付いたのはそれから数日後。\r\n自分の周りで噂になっていることが、そこに書かれていた。\r\n内容に不信を抱いたのは更に数日後。\r\n自分の身近で起きる内容を予言され、それが的中した。\r\n内容に恐怖を覚えたのは、水代葵との肉体関係を結んだ直後。\r\nどうしてそのことが書かれているんだ！？', '<a href=\"http://www.comshop.co.jp/g-haku/index.php?jan=4517451000252\" target=\"_blank\"><cite title=\"ゲーム博物館\">パッケージ</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:47'),
(226, '螺旋回廊2', 'らせんかいろう2', 'らせんかいろう20', 9, 294, '', 45, 2, 379, '標的となった女性たちを誘拐して監禁・凌辱を行い、その非道な行為をコンテンツとして克明に掲載しているインターネットサイト『EDEN』。\r\nこのサイトに関わる者たちは、一連の鬼畜的な凌辱行為を「遊び」と称し、その歪んだ欲望の赴くままに「遊び」に興じている。\r\nこの『EDEN』が、次なる標的を選び、猟奇的な犯罪者たちが再びその魔手を伸ばし始めた時、前作を上回る非道で残虐な宴が開始される。\r\n狙われるのはあなたの身近にいる大切な女性たち――。\r\nインターネットを媒介とした、実態の見えない狂気が、再びあなたに襲いかかる……。', '<a href=\"http://www.comshop.co.jp/g-haku/index.php?jan=4517451000818\" target=\"_blank\"><cite title=\"ゲーム博物館\">パッケージ</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:47'),
(227, '瀬里奈', 'せりな', 'せりな', 3, 144, '', NULL, NULL, 383, '主人公の高阪直人は、半年前から失踪中の恩師の大学教授から一通の手紙を受け取る。手紙の内容に興味を持った直人は記されていた上森村に行くことを決意する。村はかなりの山奥にあり、交通手段も限られていて一日2本のバスが運行しているのみ。\r\n長い時間待たされて乗ったそのバスの中で、彼は周防瀬里奈と知り合う。瀬里奈もまた手紙を受け取っていて、親や姉が住んでいる上森村に向かっていたのだという。\r\n彼女と共に上森村を訪れたのだが、住民の態度がやけによそよそしい。おかしな雰囲気に戸惑いながらも、直人はフィールドワークを始める。\r\nいつしか瀬里奈も行動を共にし始め、やがて二人の関係に変化が訪れる…', '<a href=\"https://ja.wikipedia.org/wiki/%E7%80%AC%E9%87%8C%E5%A5%88_(%E3%82%B2%E3%83%BC%E3%83%A0)\" target=\"_blank\"><cite title=\"Wikipedia 2013年6月22日 (土) 07:51 更新版\">Wikipedia</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:37:28'),
(228, '月陽炎 ～つきかげろう～', 'つきかげろう', 'つきかげろう', 4, 165, '', NULL, NULL, 385, '時代は大正、季節は紅や黄色の木の葉が舞い降りるとき。\r\n父に言われて、帝都から一人の青年がやって来た。\r\n好奇心に胸を躍らせていた青年は、少女に出会い、やがて心を通わす。だが、それは偶然ではなく必然の恋だった。\r\nときが経てば青年はこの街にやって来た本当の理由を知ることになるだろう。\r\n\r\nほんのひとかけらの幸せを追い求めた、少女と青年の物語が、今そっと幕を開ける…。', '<a href=\"http://www.getchu.com/soft.phtml?id=8300\" target=\"_blank\">Getchu.com</a>', '2018-03-24 10:17:50', '2018-05-29 22:37:28'),
(230, 'SILENT HILL -SHATTERED MEMORIES-', 'さいれんとひるしゃったーどめもりーず', 'さいれんとひるしゃったーどめもりーず', 3, 108, 'ホラーアドベンチャー', 16, 7, 399, 'ハリー・メイソンは、娘のシェリルと車を走らせている途中、過って事故を起こしてしまう。\r\n気を失ってしまったハリーが目を覚ますと、いつの間にかシェリルの姿がなくなっていた。\r\n\r\n行方不明になったシェリルの手掛かりを探し、雪の降るサイレントヒルを彷徨い歩くうち、ハリーは、みるみると変貌していく恐ろしい\"氷の世界\"に対面し、驚愕する。\r\n\r\n娘を探しながら、異形の者が徘徊する街「SILENT HILL」の真実に直面したとき、ハリーに、そして「SILENT HILL」に待っている結末とは。', '<a href=\"http://www.konami.jp/products/silenthill_sm_ps2/\" target=\"_blank\">PS2版公式サイト</a>', '2018-03-24 10:17:50', '2018-05-29 22:36:48'),
(231, '『超』怖い話DS 青の章', 'ちょうこわいはなしでぃーえすあおのしょう', 'ちょうこわいはなしでぃーえすあおのしょう', 4, 164, '怪談アドベンチャーノベル', NULL, NULL, 400, '数々の恐怖体験で単行本はシリーズ累計100万部を突破。\r\nTV版やネットシネマ、劇場公開と展開されている人気シリーズ“「超」怖い話”がニンテンドーDSで登場します。\r\n“「超」怖い話”は 「怪談アドベンチャーノベル」 です。\r\nあなたはシリーズ原作者でもある平山夢明氏と共に都内を巡りながら、さまざまな人から怪異現象の体験談を聞くことになります。\r\nシナリオの山場となる場面ではグラフィック・サウンドによる演出を用意し、怪談独特の緊張感・臨場感を盛り上げます。', '<a href=\"https://www.nintendo.co.jp/ds/software/bkaj/index.htmlm\" target=\"_blank\">DSソフトウェア</a>', '2018-03-24 10:17:50', '2018-05-29 22:36:49'),
(232, 'クリオスタシス', 'くりおすたしす', 'くりおすたしす', 2, 71, 'FPSホラーアドベンチャー', NULL, NULL, 401, '1981年- 北極。そこは極寒の氷の世界。\r\n北極観測隊としてその地を訪れていた気象学者「アレキサンダー ネステロフ」。\r\n荒れる吹雪の日。\r\n彼は偶然にも難破した原子力砕氷船「ノースウィンド号」を発見する。\r\n荒れたその船体からひと目で、永久凍土に長年漂っていたことが感じ取れた。\r\n悪天候の中、静かに、そして孤独にそびえる巨大砕氷船。\r\n絶望感と恐怖心、そしてわずかな好奇心がアレキサンダーの足を船内へと向かわせた…。\r\n未だ体験したことのない凍てつく恐怖が迫るとも知らずに…。', '<a href=\"http://cryostasis.zoo.co.jp/\" target=\"_blank\">公式サイト</a>', '2018-03-24 10:17:50', '2018-05-29 22:36:48'),
(233, 'キャサリン', 'きゃさりん', 'きゃさりん10', 2, 60, 'アクションアドベンチャー', 50, 1, 408, '若い男たちの奇怪な死。それにまつわる不気味な噂。\r\n落ちる夢を見たとき、すぐに目覚めないと本当に死んでしまうらしい。\r\n物騒なニュースが街を賑わす最中、主人公ヴィンセントのもとに見知らぬ美女が現れる。\r\n彼女の名は「キャサリン」。\r\n奇しくも5年の付き合いになる恋人と“同じ名前”をしたその美女と、ヴィンセントは一夜の過ちをおかし……。\r\n──そして、悪夢の夜が始まるのだった。', '<a href=\"https://www.atlus.co.jp/title-archive/cathy/\" target=\"_blank\">公式サイト</a>', '2018-03-24 10:17:50', '2018-05-29 22:36:48'),
(234, 'イケニエノヨル', 'いけにえのよる', 'いけにえのよる', 1, 20, '呪いのゲーム', NULL, NULL, 409, '憑夜見(ツクヨミ)渓谷。\r\nそこは、神に生け贄を捧げていたという伝承が残る地。\r\n\r\nとある大学の仲間5人が、この渓谷に建つ館に集まることになっていた。\r\n楽しい思い出になるはずの小旅行。\r\n\r\nしかし、運命は残酷だった。\r\n\r\n”オネネチャンヲ　タスケテ”\r\n少女の囁きが聞こえたとき、シズカに、惨劇のマクガアガル。', '<a href=\"https://www.marv.jp/special/game/wii/ikenie/\" target=\"_blank\"><cite title=\"公式サイト\">公式サイト</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:48'),
(235, 'F.E.A.R.3', 'ふぃあー3', 'ふぃあー30', 6, 256, 'アクション・ホラーFPS', 19, 3, 431, 'アルマの中に宿る命…\r\n迫りくる新たな恐怖…\r\n血塗られた戦場で交わる殺人鬼と化した弟との運命。\r\n共に戦うか、アルマと対峙して過酷な任務にひとり挑むのか。\r\n恐怖を超越した血のつながりを目撃せよ。', '<a href=\"http://www.comshop.co.jp/g-haku/index.php?jan=4988135861085\" target=\"_blank\"><cite title=\"ゲーム博物館\">パッケージ</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:50'),
(236, 'ひぐらしのなく頃に', 'ひぐらしのなくころに', 'ひぐらしのなくころに10', 6, 244, 'サウンドノベル', 46, 1, 410, '昭和５８年６月の雛見沢村へようこそ\r\n\r\nあなたは主人公の視点を通して、この村での生活を楽しむことができます。\r\n主人公『前原圭一』は、引越してきたばかり。\r\nかつてのダム闘争で寂れたこの村は、新しい村人が増え、村が賑やかになることを快く歓迎してくれます。\r\n引越してきたばかりのあなたは、友人もなく心細いでしょう。\r\nでも、歳の近いクラスメートたちはあなたを放っておきません。\r\nあなたも持ち前の明るい性格で、それらにすぐ馴染むことでしょう。', '<a href=\"http://07th-expansion.net/hig_gensaku\" target=\"_blank\">公式サイト</a>', '2018-03-24 10:17:50', '2018-05-29 22:37:28'),
(239, 'うみねこのなく頃に', 'うみねこのなくころに', 'うみねこのなくころに20', 1, 33, 'サウンドノベル', 47, 1, NULL, '伊豆諸島、六軒島。\r\n全長１０ｋｍにも及ぶこの島が、観光パンフに載ることはない。\r\nなぜなら、大富豪の右代宮家が領有する私的な島だからである。\r\n\r\n年に一度の親族会議のため、親族たちは島を目指していた。\r\n\r\n議題は、余命あと僅かと宣告されている当主、金蔵の財産分割問題。\r\n\r\n天気予報が台風の接近を伝えずとも、島には確実に暗雲が迫っていた…。', '<a href=\"http://07th-expansion.net/umi_gensaku\" target=\"_blank\">公式サイト</a>', '2018-03-24 10:17:50', '2018-05-29 22:37:29'),
(242, 'アパシー　学校であった怖い話 〜Visual Novel Version〜', 'あぱしーがっこうであったこわいはなしびじゅあるのべるばーじょん', 'あぱしーがっこうであったこわいはなしびじゅあるのべるばーじょん', 1, 11, 'ホラー・ビジュアルノベル', 34, 10, 417, '1995年6月。\r\n新聞部は、夏休み前の学校新聞で「学校の七不思議」を特集することになった。\r\n七人の学生を集めて、彼らから怖い話を聞き、それを特集としてまとめるのだ。\r\n新聞部三年生の日野から命を受け、新入部員の坂上修一はその集会の聞き役を担当することになった。\r\n集会当日、放課後の新聞部部室に日野によって集められた七人の語り部と聞き役の坂上修一は初めて顔を合わせる。\r\nしかし、七人集められたはずの語り部は、何故か六人しかいなかった。\r\n七人目が姿を見せぬまま始まる七不思議の集会。\r\n七人目は、現れるのか。\r\nそして、この集会の終わりに待っているのは……。', '<a href=\"http://www.takiya.jp/gakkowa/\" target=\"_blank\"><cite title=\"公式サイト\">公式サイト</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:47'),
(243, '男子校であった怖い話', 'だんしこうであったこわいはなし', 'だんしこうであったこわいはなし', 4, 163, 'アドベンチャー', NULL, NULL, 410, '五年前、この学校では夏休みに行方不明になった四人の生徒達がいた。\r\nその消息は、現在も分かっていない。\r\n私立笹ヶ岡学園高等学校に伝わる、―――都市伝説。', '<a href=\"http://www.takiya.jp/78/boy/index.htm\" target=\"_blank\">公式サイト</a>', '2018-03-24 10:17:50', '2018-05-29 22:36:49'),
(245, 'シャドウ オブ ザ ダムド', 'しゃどうおぶざだむど', 'しゃどうおぶざだむど', 3, 128, 'アクション', NULL, NULL, 420, '“デーモンハンター”を生業とする主人公、ガルシア・ホットスパーは地獄の帝王を仕留める。\r\nが、それがきっかけとなり、最愛の女性ポーラが地獄へと連れ去られることに。\r\n恋人ポーラを救出するため、すべての邪悪が集う地獄へと向かうガルシア。\r\n彼と歴戦を共にしてきた奇妙な相棒、髑髏（ドクロ）のジョンソンと「闇」を切り裂く「光」を手に、予測のつかない旅が始まる・・・。', '<a href=\"http://www.jp.playstation.com/software/title/bljm60358.html\" target=\"_blank\"><cite title=\"プレイステーションオフィシャルサイト\">PSソフトウェアカタログ</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:49'),
(246, '零～眞紅の蝶～', 'ぜろしんくのちょう', 'ぜろ41', 3, 149, 'アクションアドベンチャー', 2, 5, 452, 'それは、決して見てはいけない禁断の儀式…\r\n    \r\n一夜にして地図から消えたという皆神村。\r\n悪霊が彷徨う廃村に迷い込んだ双子の姉妹・澪と繭は、双子を贄とする儀式の秘密を知ることになる…。', '<a href=\"http://www.comshop.co.jp/g-haku/index.php?jan=4902370519501\" target=\"_blank\"><cite title=\"ゲーム博物館\">パッケージ</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:47'),
(247, 'うみねこのなく頃に ～魔女と推理の輪舞曲～', 'うみねこのなくころに40', 'うみねこのなくころに23', 1, 34, '連続殺人幻想ノベル', 47, 4, 422, '1986年10月4日の六軒島へようこそ。\r\n貴方は、右代宮家が年に一度開く親族会議を垣間見ることができます。\r\n莫大な資産を溜め込んだ老当主はもはや余命も僅か。\r\n息子兄弟たちにとって、親族会議最大の争点はその遺産の分配についてでした。\r\n誰もが大金を欲しており、誰もが譲らない。誰も信じない。\r\n老当主の莫大な資産を得るのは誰か。老当主が隠し持つという黄金10tの在り処はどこか。\r\nその在り処を示すという不気味な碑文の謎は解けるのか。\r\nそんな最中、魔女を名乗る人物から届く怪しげな手紙。\r\n18人しかいないはずの島に19人目の気配。\r\n繰り返される残酷な殺人と、現場に残される不可解な謎。\r\n何人死ぬのか。何人生き残るのか。\r\nそれとも全員死ぬのか。\r\n犯人は18人の中にいるのか、いないのか。\r\nそして犯人は「人」なのか、「魔女」なのか。', '<a href=\"http://www.comshop.co.jp/g-haku/index.php?jan=4562168541734\" target=\"_blank\"><cite title=\"ゲーム博物館\">PS3版パッケージ</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:37:29'),
(248, 'BIOHAZARD REVELATIONS', 'ばいおはざーどりべれーしょんず', 'ばいおはざーどりべれーしょんず10', 6, 225, 'サバイバルホラー', 1, 9, 424, '2005年、欧州洋上…\r\n夜の海原に雷鳴が轟くなか、一隻のタグボードが波間を進んでいた。\r\nその船上には捜索指令を受けて出動したBSAA隊員の姿があった。\r\n眼前にそそり立つ船影は、ゴーストシップそのものと化したかつての豪華客船のものである。\r\n\r\nゴーストシップに近づきながら、装備を確認し終えたジルがつぶやく。\r\n「この船の何処かに…きっと、彼らはいる…」\r\n\r\n…だが、この失踪事件は悪夢の始まりにすぎなかった。\r\n\r\nそして背後で渦巻く陰謀に囚われ始めていることをジルはまだ知らずにいた…。', '<a href=\"http://www.capcom.co.jp/bio_series/biohazard_revelations.html\" target=\"_blank\">旧公式サイト</a>', '2018-03-24 10:17:50', '2018-05-29 22:36:50'),
(249, 'バイオハザード オペレーションラクーンシティ', 'ばいおはざーどおぺれーしょんらくーんしてぃ', 'ばいおはざーどおぺれーしょんらくーんしてぃ', 6, 222, 'TPS', 1, 22, 433, '黒一色の部隊が静かに降り立った\r\n\r\n特殊部隊「U.S.S.」の目標は街の地下にある研究施設。\r\n合流したハンクという男の指示のもと、彼らは突き進む。\r\nそこで研究されていた”それ”は、世界を揺り動かす価値があり、そして世界を滅ぼすことも出来る力があった。\r\nすべての障害を排除して、”それ”を手に入れる。彼らにとって簡単な仕事のはずだった。\r\n\r\nしかし、この作戦が彼らの運命を無理やり捻じ曲げていく。', '<a href=\"http://www.capcom.co.jp/bhorc/\" target=\"_blank\">公式サイト</a>', '2018-03-24 10:17:50', '2018-05-29 22:36:49'),
(251, 'Rise of Nightmares', 'らいずおぶないとめあ', 'らいずおぶないとめあ', 9, 289, '体感ホラーアドベンチャー', NULL, NULL, 437, 'ソビエト時代の古びた列車が暗いルーマニアの森を走り抜ける。\r\nその列車のコンパートメントに座る旅行中の夫婦。\r\n2人の些細な諍いからこの物語は始まる。\r\n乗り合わせた様々な乗客たちと共に運命の悲劇に巻き込まれていくとも知らずに……', '<a href=\"https://game.watch.impress.co.jp/docs/review/478793.html\" target=\"_blank\"><cite title=\"GAME Watch\">GAME Watch</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:48'),
(252, 'コープスパーティー ブラッドカバーリピーティッドフィアー', 'こーぷすぱーてぃーぶらっどかばーりぴーてぃっどふぃあー', 'こーぷすぱーてぃー10', 2, 85, '廃校監禁ホラーアドベンチャー', 48, 1, 438, '血塗られた夜の廃校監禁ツアーとは・・・\r\n\r\n呪われた学び舎「天神小学校」で起こった、児童四名、連続誘拐・殺害事件。\r\n三十年前に封印された骸（むくろ）の祭りが、現代に蘇る！\r\n\r\n怪談好きの委員長が見つけてきたおまじない「幸せのサチコさん」。\r\nその正体は過去の凄惨な殺人事件により廃校となった、異空間「天神小学校」へといざなう呪いの儀式であった。\r\n\r\n果たして、古ぼけた旧校舎に監禁され生殺与奪の権を握られた生徒たちの運命は・・・・・・？', '<a href=\"http://5pb.jp/games/corpse/index.php\" target=\"_blank\">公式サイト</a>', '2018-03-24 10:17:50', '2018-05-29 22:36:48'),
(253, 'コープスパーティー Book of Shadows', 'こーぷすぱーてぃーぶっくおぶしゃどうず', 'こーぷすぱーてぃー20', 2, 86, '廃校監禁ホラーアドベンチャー', 48, 2, 440, '本作は、「コープスパーティー ブラッドカバー リピーティッドフィアー」から派生した様々な物語を集め、1つの作品として纏めたものです。\r\nそれぞれが単発でありながらも、BRを中心に関連付けされているサテライトエピソード集といった内容です。', '<a href=\"http://corpse.jp/bs/\" target=\"_blank\">公式サイト</a>', '2018-03-24 10:17:50', '2018-05-29 22:36:48'),
(254, '真かまいたちの夜　11人目の訪問者', 'しんかまいたちのよるじゅういちにんめのほうもんしゃ', 'しんかまいたちのよるじゅういちにんめのほうもんしゃ', 3, 130, 'サウンドノベル', 29, 10, 444, '岩手県・遠野市。\r\n作家志望の主人公、坂巻快人（さかまきかいと）は、小説の題材を探しに、遠野のペンションを訪れる。\r\n宿泊予定者は10人だったはずなのに、宿泊客は、なぜか11人……。\r\nそのうちの１人が露天風呂で殺される。\r\n快人は、偶然再会したかつての友人・立花京香とともに、事件の真相を探りはじめる。\r\nしかし、ひとつ、またひとつと死体が増えていき……。', '<a href=\"http://www.spike-chunsoft.co.jp/shinkama/\" target=\"_blank\">公式サイト</a>', '2018-03-24 10:17:50', '2018-05-29 22:36:49'),
(255, 'うみねこのなく頃に散　～真実と幻想の夜想曲～', 'うみねこのなくころにさんしんじつとげんそうのやそうきょく', 'うみねこのなくころに50', 1, 35, '連続殺人幻想ノベル', 47, 6, 446, '孤島の洋館で幾度も繰り返される連続猟奇殺人と魔女幻想。\r\nしかし散らぬ花はないように、魔女幻想もやがて必ず散る。\r\n挑めばミステリー。屈せばファンタジー。\r\n貴方は魔女幻想を打ち破り、真相に至ることができるか!?', '<a href=\"http://www.comshop.co.jp/g-haku/index.php?jan=4562168541888\" target=\"_blank\"><cite title=\"ゲーム博物館\">PS3版パッケージ</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:37:29'),
(256, 'DEAD ISLAND', 'でっどあいらんど', 'でっどあいらんど10', 4, 176, 'アクション・RPG', 52, 1, 448, 'うずまく恐怖。暴力。狂気。混乱。美しき南国の楽園は地獄と化した！\r\n突如ゾンビに覆い尽くされた南の島は、混沌の地へと姿を変える。\r\n外界との連絡は一切絶たれた。\r\n生き残る道はただひとつしかない。\r\nそれは、死に物狂いで戦い抜き、島を脱出すること――', '<a href=\"http://deadisland.zoo.co.jp/\" target=\"_blank\">PC版公式サイト</a>', '2018-03-24 10:17:50', '2018-05-29 22:36:49'),
(257, '心霊カメラ ～憑いてる手帳～', 'しんれいかめらついてるてちょう', 'しんれいかめらついてるてちょう', 3, 136, 'ホラー', NULL, NULL, 451, 'ノートを覗くと開かれる異界への扉。\r\n\r\nニンテンドー3DSで、ARノートを写しながらプレイするホラーゲーム。\r\nAR(拡張現実)によって、ノートの中で様々な怪奇現象が起こります。', '<a href=\"http://www.comshop.co.jp/g-haku/index.php?jan=4902370519174\" target=\"_blank\"><cite title=\"ゲーム博物館\">パッケージ</cite></a>', '2018-03-24 10:17:50', '2018-05-29 22:36:49'),
(258, 'BIOHAZARD6', 'ばいおはざーど6', 'ばいおはざーど0060', 6, 216, 'サバイバルホラー', 1, 8, 453, 'ゾンビが闊歩するアメリカ【トールオークス】にて、大統領暗殺の容疑をかけられたレオン。\r\n要人救出のため、バイオハザードが発生している中国【蘭祥】にて作戦を遂行するクリス。\r\n欧州の紛争地域である【イドニア共和国】にて、正体不明のB.O.W.に追われるジェイク。\r\nそして3人の前にあらわれる、謎の女エイダ。 \r\nそれぞれの場所、立場で、運命は絡み合っていく。', '<a href=\"http://www.capcom.co.jp/bio6/index.html\" target=\"_blank\">公式サイト</a>', '2018-03-24 10:17:50', '2018-05-29 22:36:49'),
(259, '闇からのいざない TENEBRAE I', 'やみからのいざないてねぶらい1', 'やみからのいざないてねぶらい1', 8, 282, 'ホラーアドベンチャー', 49, 1, 467, 'フリーのルポライター・犬上征爾の元にある夜「ニューアイルランドシティで取材を」という電話がかかってくる。\r\nニューアイランドシティに向かった犬上は、旧知の間柄である刑事、“三枝草・パトリシア・薫”と偶然再会する。\r\n談笑している彼らの耳に届く、いくつもの悲鳴。\r\n見ると、どこから湧き上がったのか、見たことも無い黒い霧が全てを覆い尽くすように広がっていた。\r\n周囲の人間を飲み込みながら迫る、黒い霧。\r\n黒い霧はいったい何なのか？\r\nそして、二人は霧から逃げることが出来るのか？', '<a href=\"http://www.jp.playstation.com/software/title/uljm06147.html\" target=\"_blank\"><cite title=\"プレイステーションオフィシャルサイト\">PSソフトウェアカタログ</cite></a> ※短縮のため削減・改変', '2018-03-24 10:17:50', '2018-05-29 22:37:28'),
(263, 'ゾンビU', 'ぞんびゆー', 'ぞんびゆー', 3, 153, 'サバイバルホラーアクション', NULL, NULL, 469, '2012年、ロンドン。\r\nあなたは荒廃した街の通りで目を覚ます。\r\n辺りには死体が散乱し、ゾンビと化した人間が肉を求めてさまよっている。\r\n\r\n果たしてあなたは、この未曾有の厄災に見舞われたロンドンから無事脱出することができるのだろうか……。', '<a href=\"http://www.ubisoft.co.jp/zombiu/\" target=\"_blank\">公式サイト</a> ※短縮のため一部削減', '2018-03-24 10:17:50', '2018-05-29 22:36:49'),
(264, 'SILENT HILL: DOWNPOUR', 'さいれんとひるだうんぷあー', 'さいれんとひる50', 3, 107, 'ホラーアドベンチャー', 16, 8, 470, '凶悪犯罪者用の刑務所へ移送されることになった、囚人のマーフィー・ペンドルトン。\r\nその途中、移送バスが横転。\r\n大破したバスから投げ出され自由の身となったマーフィーは、引き込まれるようにサイレントヒルへと入っていく。\r\nそれが、終わりのない悲劇のはじまりとも知らずに。\r\n陰鬱で不可解な物語に潜む、多くの謎。\r\n進むほどに狂気は加速し、DOWNPOUR(土砂降りの雨)がすべてを奪っていく―――。', '<a href=\"http://www.konami.jp/gs/game/shdp/\" target=\"_blank\">公式サイト</a>', '2018-03-24 10:17:50', '2018-05-29 22:37:29'),
(265, 'BIOHAZARD7 -Resident Evil-', 'ばいおはざーど7れじでんといーびる', 'ばいおはざーど0070', 6, 217, '', 1, NULL, 488, '3年前に消息を絶った妻から届いた一通のメール。\r\n導かれるまま主人公＝イーサン・ウィンターズは遠くルイジアナへと自動車を走らせ、荒れた邸にたどりつく。\r\n妻の痕跡を求め、異様な気配が漂う邸内を捜索するが、突然何者かの襲撃を受け、意識を失ってしまう…。\r\n目覚めたイーサンを待っていたのは、狂気で満たされた邸の住人ベイカー一家だった。', '<a href=\"http://www.capcom.co.jp/biohazard7\" target=\"_blank\">公式サイト</a>', '2018-03-24 10:18:11', '2018-05-29 22:36:49'),
(266, 'Dead by Daylight', 'でっどばいでいらいと', 'でっどばいでいらいと', 4, 182, '', NULL, NULL, 491, '『Dead by Daylight』は、プレイヤーが残忍なキラー（1人）と、キラーに捕まって殺されないように逃げ回るサバイバー（4人）に分かれて対戦する、4対1のマルチプレイヤーホラーゲームです。', '<a href=\"http://store.steampowered.com/app/381210/Dead_by_Daylight/\" target=\"_blank\">Steam</a>', '2018-03-24 10:18:11', '2018-05-29 22:36:49'),
(267, 'Emily Wants to Play', 'えみりーうぉんつとぅーぷれい', 'えみりーうぉんつとぅーぷれい', 1, 43, '', NULL, NULL, 492, 'ピザ配達員であるプレーヤーは、ピザを届けに来た家に閉じ込められてしまう。\r\nそこに現れる謎の少女と人形。\r\n彼女たちから耐えつつ、生きて脱出することはできるのか・・・。', '<a href=\"http://nippon1.jp/consumer/indie_spirits/emily_wants_to_play/\" target=\"_blank\"><cite title=\"公式サイト\">PS4版公式サイト</cite></a>', '2018-03-24 10:18:11', '2018-05-29 22:36:48'),
(268, 'NightCry', 'ないとくらい', 'ないとくらい', 5, 199, '', NULL, NULL, 495, '豪華客船の旅に出発した主人公（プレイヤー）。\r\n華やかな雰囲気の中、楽しい旅になると思われていたが、船内で殺害事件が発生し、船客の間に猜疑心が生まれていく…。\r\n夜になると通路に響き渡る、不気味な赤ん坊の泣き声。\r\n闇の住人によってひとりずつ殺されていく船客たち。\r\n次の犠牲者は一体誰なのか…！\r\n逃げ場の無い船上で助かる術は、主人公（プレイヤー）の選択ひとつ…', '<a href=\"http://store.steampowered.com/app/427660/NightCry/\" target=\"_blank\">Steam</a>', '2018-03-24 10:18:11', '2018-05-29 22:36:49'),
(269, '@SIMPLE DLシリーズ Vol.5　THE 呪いの廃病院　～閉じ込められた顔のない少女～', 'ざのろいのはいびょういんとじこめられたかおのないしょうじょ', 'ざのろいのはいびょういんとじこめられたかおのないしょうじょ', 3, 115, '', NULL, NULL, 496, '「この建物に出口はない。」\r\n廃病院で出会った詩穂という少女は言った。\r\n信じられないことだが彼女は顔がなかった。\r\n霊能者が残したメモには「魂の写真を集めてほしい。あの子を救えるかもしれない。」と記されていた。\r\nさらに「あの看護師には気をつけろ。」とも書いてあった。\r\n呪われた廃病院で今、恐怖の幕が上がる。 ', '<a href=\"https://www.nintendo.co.jp/titles/50010000011886\" target=\"_blank\">任天堂紹介ページ</a>', '2018-03-24 10:18:11', '2018-05-29 22:36:48'),
(270, 'Until Dawn - 惨劇の山荘 -', 'あんてぃるどーんさんげきのさんそう', 'あんてぃるどーんさんげきのさんそう', 1, 19, '', NULL, NULL, 497, '雪降り積もる山荘に閉ざされた、8人の若者達。\r\n\r\n楽しいはずの休暇は、やがて恐怖の夜へと変貌する。\r\n自分たちしかいないはずの山荘を脅かす謎の存在。\r\n次々と起こる怪奇現象。吹雪によって閉ざされた逃げ場のない雪山。\r\n\r\n彼らは果たして、忍び寄る死の運命から逃れることができるのだろうか…。', '<a href=\"http://www.jp.playstation.com/scej/title/untildawn/\" target=\"_blank\"><cite title=\"公式サイト\">公式サイト</cite></a>', '2018-03-24 10:18:11', '2018-05-29 22:36:48'),
(271, 'WHITEDAY～学校という名の迷宮～', 'ほわいとでいがっこうというなのめいきゅう', 'ほわいとでいがっこうというなのめいきゅう', 6, 266, '', NULL, NULL, 499, '転校した学校の怪談話は「実話」だった。\r\n真夜中の「八城高校」を舞台に迫りくる幽霊からヒロインたちと共に生き延びろ。\r\n固く閉ざされた呪われた学校から脱出することはできるのか？\r\n逃げることしかできない恐怖の体験が始まる……。', '<a href=\"http://www.comshop.co.jp/g-haku/index.php?jan=4510772170079\" target=\"_blank\"><cite title=\"ゲーム博物館\">パッケージ</cite></a>', '2018-03-24 10:18:11', '2018-05-29 22:36:50');
